import type { Location } from '@/lib/maps/types';
import { useEffect, useMemo, useState } from 'react';
import { isTruthy } from '@petspark/shared';
import type { MapMarker } from './LazyInteractiveMap';

// Dynamic imports for leaflet to ensure code-splitting
const loadLeaflet = async () => {
    const [L, ReactLeaflet] = await Promise.all([
        import('leaflet'),
        import('react-leaflet')
    ]);

    // Load CSS dynamically
    await import('leaflet/dist/leaflet.css');

    return { L, ReactLeaflet };
};

const DEFAULT_ICON = {
    iconUrl:
        'data:image/svg+xml;base64,',
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32],
};

interface LeafletMapProps {
    center: Location;
    zoom?: number;
    markers?: MapMarker[];
    onMarkerClick?: (marker: MapMarker) => void;
    onMapClick?: (location: Location) => void;
    className?: string;
    height?: string;
    interactive?: boolean;
    clusterMarkers?: boolean;
}

export default function LeafletMap({
    center,
    zoom = 13,
    markers = [],
    onMarkerClick,
    onMapClick,
    className = '',
    height = '100%',
    interactive = true,
    clusterMarkers = true,
}: LeafletMapProps): React.JSX.Element {
    const [mapComponents, setMapComponents] = useState<{
        L: any;
        ReactLeaflet: any;
    } | null>(null);

    useEffect(() => {
        loadLeaflet().then(({ L, ReactLeaflet }) => {
            setMapComponents({ L, ReactLeaflet });
        });
    }, []);

    if (!mapComponents) {
        return (
            <div className="flex items-center justify-center h-full bg-muted/20">
                <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full" />
            </div>
        );
    }

    const { L, ReactLeaflet } = mapComponents;
    const { MapContainer, Marker, Popup, TileLayer, useMap, useMapEvents } = ReactLeaflet;

    // Create default icon
    const defaultIcon = useMemo(() => L.icon(DEFAULT_ICON), [L]);

    function MapController({
        center: mapCenter,
        zoom: mapZoom,
        onMapClick: handleMapClick,
    }: {
        center: Location;
        zoom: number;
        onMapClick?: (location: Location) => void;
    }): null {
        const map = useMap();

        useEffect(() => {
            map.setView([mapCenter.lat, mapCenter.lng], mapZoom);
        }, [map, mapCenter.lat, mapCenter.lng, mapZoom]);

        useMapEvents({
            click: (event: any) => {
                if (!isTruthy(handleMapClick)) return;
                handleMapClick({
                    lat: event.latlng.lat,
                    lng: event.latlng.lng,
                });
            },
        });

        return null;
    }

    const clusteredMarkers = useMemo(() => {
        if (!clusterMarkers || markers.length === 0) return markers;

        const clusters: MapMarker[] = [];
        const processed = new Set<string>();
        const clusterRadius = 0.01;

        markers.forEach((marker) => {
            if (processed.has(marker.id)) return;

            const nearby = markers.filter((m) => {
                if (processed.has(m.id)) return false;
                const latDiff = Math.abs(m.location.lat - marker.location.lat);
                const lngDiff = Math.abs(m.location.lng - marker.location.lng);
                return latDiff < clusterRadius && lngDiff < clusterRadius;
            });

            if (nearby.length > 1) {
                const clusterCenter: Location = {
                    lat: nearby.reduce((sum, m) => sum + m.location.lat, 0) / nearby.length,
                    lng: nearby.reduce((sum, m) => sum + m.location.lng, 0) / nearby.length,
                };

                clusters.push({
                    id: `cluster-${String(marker.id ?? '')}`,
                    location: clusterCenter,
                    data: nearby,
                    icon: L.divIcon({
                        className: 'custom-cluster-icon',
                        html: `<div class="cluster-marker">${String(nearby.length ?? '')}</div>`,
                        iconSize: [40, 40],
                        iconAnchor: [20, 20],
                    }),
                });

                nearby.forEach((m) => processed.add(m.id));
            } else {
                clusters.push(marker);
                processed.add(marker.id);
            }
        });

        return clusters;
    }, [markers, clusterMarkers, L]);

    return (
        <div className={`relative ${String(className ?? '')}`} style={{ height }}>
            <style>{`
        .leaflet-container {
          font-family: inherit;
          z-index: 0;
        }
        .cluster-marker {
          background: hsl(var(--primary));
          color: hsl(var(--primary-foreground));
          border-radius: 50%;
          width: 40px;
          height: 40px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-weight: bold;
          font-size: 14px;
          border: 3px solid white;
          box-shadow: 0 2px 8px rgba(0,0,0,0.3);
        }
        .custom-cluster-icon {
          background: transparent;
          border: none;
        }
      `}</style>
            <MapContainer
                center={[center.lat, center.lng]}
                zoom={zoom}
                style={{ height: '100%', width: '100%' }}
                scrollWheelZoom={interactive}
                zoomControl={interactive}
                dragging={interactive}
                touchZoom={interactive}
                doubleClickZoom={interactive}
            >
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <MapController
                    center={center}
                    zoom={zoom}
                    {...(onMapClick ? { onMapClick } : {})}
                />
                {clusteredMarkers.map((marker) => (
                    <Marker
                        key={marker.id}
                        position={[marker.location.lat, marker.location.lng]}
                        icon={marker.icon ?? defaultIcon}
                        eventHandlers={{
                            click: () => {
                                if (isTruthy(onMarkerClick)) {
                                    onMarkerClick(marker);
                                }
                            },
                        }}
                    >
                        {marker.popup ? <Popup>{marker.popup}</Popup> : null}
                    </Marker>
                ))}
            </MapContainer>
        </div>
    );
}

'use client';
import { useState, useEffect, useMemo, useCallback, useRef } from 'react';
import { useStorage } from '@/hooks/use-storage';
import { Button } from '@/components/ui/button';
import { Bell, BellRinging } from '@phosphor-icons/react';
import { Badge } from '@/components/ui/badge';
import {
  useSharedValue,
  useAnimatedStyle,
  withTiming,
  withRepeat,
  withSequence,
  interpolate,
  Extrapolation,
  MotionView,
} from '@petspark/motion';
import { isTruthy } from '@petspark/shared';
import { haptics } from '@/lib/haptics';
import { createLogger } from '@/lib/logger';
import { cn } from '@/lib/utils';
import { PremiumNotificationCenter } from './PremiumNotificationCenter';
import type { PremiumNotification } from './types';
import type { AnimatedStyle } from '@petspark/motion';

const logger = createLogger('PremiumNotificationBell');

export function PremiumNotificationBell(): JSX.Element {
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const [notifications] = useStorage<PremiumNotification[]>('premium-notifications', []);
  const [hasNewNotification, setHasNewNotification] = useState<boolean>(false);
  const [lastCheckTime, setLastCheckTime] = useStorage<number>(
    'last-notification-check',
    Date.now()
  );
  const notificationTimerRef = useRef<NodeJS.Timeout | null>(null);

  const allNotifications = useMemo<PremiumNotification[]>(
    () => notifications ?? [],
    [notifications]
  );

  const unreadCount = useMemo<number>(
    () => allNotifications.filter((n) => !n.read && !n.archived).length,
    [allNotifications]
  );

  const urgentCount = useMemo<number>(
    () =>
      allNotifications.filter(
        (n) => !n.read && !n.archived && (n.priority === 'urgent' || n.priority === 'critical')
      ).length,
    [allNotifications]
  );

  const hasUrgent = useMemo<boolean>(() => urgentCount > 0, [urgentCount]);

  useEffect(() => {
    try {
      const newNotifs = allNotifications.filter((n) => n.timestamp > (lastCheckTime || 0));
      if (newNotifs.length > 0 && !isOpen) {
        setHasNewNotification(true);
        haptics.medium();
        logger.info('New notifications received', {
          count: newNotifs.length,
          urgentCount: newNotifs.filter((n) => n.priority === 'urgent' || n.priority === 'critical')
            .length,
        });

        if (notificationTimerRef.current) {
          clearTimeout(notificationTimerRef.current);
        }
        notificationTimerRef.current = setTimeout(() => {
          setHasNewNotification(false);
        }, 3000);

        return () => {
          if (notificationTimerRef.current) {
            clearTimeout(notificationTimerRef.current);
            notificationTimerRef.current = null;
          }
        };
      }
    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));
      void logger.error('Failed to check for new notifications', err);
    }
    return undefined;
  }, [allNotifications.length, lastCheckTime, isOpen, allNotifications]);

  const handleClick = useCallback((): void => {
    try {
      void haptics.medium();
      setIsOpen(true);
      setLastCheckTime(Date.now());
      setHasNewNotification(false);
      void logger.info('Notification bell clicked', { unreadCount, urgentCount });
    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));
      void logger.error('Failed to handle notification bell click', err);
    }
  }, [setLastCheckTime, unreadCount, urgentCount]);

  const handleClose = useCallback((): void => {
    setIsOpen(false);
  }, []);

  const ariaLabel = useMemo<string>(() => {
    const parts: string[] = ['Notifications'];
    if (unreadCount > 0) {
      parts.push(`${unreadCount} unread`);
    }
    if (urgentCount > 0) {
      parts.push(`${urgentCount} urgent`);
    }
    return parts.join(' - ');
  }, [unreadCount, urgentCount]);

  return (
    <>
      <Button
        variant="ghost"
        size="icon"
        onClick={handleClick}
        className="relative rounded-full w-11 h-11 hover:bg-primary/10 active:bg-primary/20 transition-colors shrink-0 touch-manipulation"
        aria-label={ariaLabel}
        aria-expanded={isOpen}
        aria-haspopup="dialog"
      >
        <BellIcon
          hasNewNotification={hasNewNotification}
          unreadCount={unreadCount}
          hasUrgent={hasUrgent}
        />

        {unreadCount > 0 && <BadgeAnimation unreadCount={unreadCount} hasUrgent={hasUrgent} />}

        {unreadCount > 0 && <RippleEffect hasUrgent={hasUrgent} />}

        {hasUrgent && <UrgentGlow />}
      </Button>

      <PremiumNotificationCenter isOpen={isOpen} onClose={handleClose} />
    </>
  );
}

interface BellIconProps {
  hasNewNotification: boolean;
  unreadCount: number;
  hasUrgent: boolean;
}

function BellIcon({ hasNewNotification, unreadCount, hasUrgent }: BellIconProps): JSX.Element {
  const rotation = useSharedValue(0);
  const scale = useSharedValue(1);
  const opacity = useSharedValue(1);

  useEffect(() => {
    if (hasNewNotification && unreadCount > 0) {
      rotation.value = withSequence(
        withTiming(-20, { duration: 100 }),
        withTiming(20, { duration: 100 }),
        withTiming(-20, { duration: 100 }),
        withTiming(20, { duration: 100 }),
        withTiming(-15, { duration: 80 }),
        withTiming(15, { duration: 80 }),
        withTiming(-10, { duration: 60 }),
        withTiming(10, { duration: 60 }),
        withTiming(0, { duration: 100 })
      );
      scale.value = withSequence(
        withTiming(1.15, { duration: 100 }),
        withTiming(1, { duration: 100 }),
        withTiming(1.15, { duration: 100 }),
        withTiming(1, { duration: 100 }),
        withTiming(1.1, { duration: 80 }),
        withTiming(1, { duration: 80 }),
        withTiming(1.05, { duration: 60 }),
        withTiming(1, { duration: 60 })
      );
    } else {
      rotation.value = withTiming(0, { duration: 200 });
      scale.value = withTiming(1, { duration: 200 });
    }
  }, [hasNewNotification, unreadCount, rotation, scale]);

  const animatedStyle = useAnimatedStyle(() => {
    return {
      transform: [{ rotate: `${rotation.value}deg`, scale: scale.value }],
      opacity: opacity.value,
    };
  }) as AnimatedStyle;

  const iconClassName = useMemo<string>(() => {
    if (hasUrgent) return 'text-destructive';
    if (unreadCount > 0) return 'text-primary';
    return 'text-foreground/80';
  }, [hasUrgent, unreadCount]);

  if (hasNewNotification && unreadCount > 0) {
    return (
      <MotionView style={animatedStyle}>
        <BellRinging size={20} weight="fill" className={iconClassName} aria-hidden="true" />
      </MotionView>
    );
  }

  return (
    <MotionView style={animatedStyle}>
      <Bell
        size={20}
        weight={unreadCount > 0 ? 'fill' : 'regular'}
        className={iconClassName}
        aria-hidden="true"
      />
    </MotionView>
  );
}

interface BadgeAnimationProps {
  unreadCount: number;
  hasUrgent: boolean;
}

function BadgeAnimation({ unreadCount, hasUrgent }: BadgeAnimationProps): JSX.Element {
  const scale = useSharedValue(0);
  const opacity = useSharedValue(0);
  const pulseScale = useSharedValue(1);

  useEffect(() => {
    scale.value = withTiming(1, { duration: 300 });
    opacity.value = withTiming(1, { duration: 300 });
  }, [scale, opacity]);

  useEffect(() => {
    if (isTruthy(hasUrgent)) {
      pulseScale.value = withRepeat(
        withSequence(withTiming(1.2, { duration: 500 }), withTiming(1, { duration: 500 })),
        -1,
        true
      );
    } else {
      pulseScale.value = withTiming(1, { duration: 200 });
    }
  }, [hasUrgent, pulseScale]);

  const badgeStyle = useAnimatedStyle(() => {
    return {
      transform: [{ scale: scale.value }],
      opacity: opacity.value,
    };
  }) as AnimatedStyle;

  const pulseStyle = useAnimatedStyle(() => {
    return {
      transform: [{ scale: pulseScale.value }],
    };
  }) as AnimatedStyle;

  return (
    <MotionView style={badgeStyle} className="absolute -top-1 -right-1">
      <MotionView style={pulseStyle}>
        <Badge
          variant={hasUrgent ? 'destructive' : 'default'}
          className="h-5 min-w-5 px-1 rounded-full text-xs font-bold flex items-center justify-center shadow-lg"
          aria-label={`${unreadCount} unread notification${unreadCount !== 1 ? 's' : ''}`}
        >
          {unreadCount > 99 ? '99+' : unreadCount}
        </Badge>
      </MotionView>
    </MotionView>
  );
}

interface RippleEffectProps {
  hasUrgent: boolean;
}

function RippleEffect({ hasUrgent }: RippleEffectProps): JSX.Element {
  const scale = useSharedValue(1);
  const opacity = useSharedValue(0.6);

  useEffect(() => {
    scale.value = withRepeat(
      withSequence(
        withTiming(1.4, { duration: hasUrgent ? 1500 : 2000 }),
        withTiming(1, { duration: 0 })
      ),
      -1,
      false
    );
    opacity.value = withRepeat(
      withSequence(
        withTiming(0, { duration: hasUrgent ? 1500 : 2000 }),
        withTiming(0.6, { duration: 0 })
      ),
      -1,
      false
    );
  }, [hasUrgent, scale, opacity]);

  const rippleStyle = useAnimatedStyle(() => {
    return {
      transform: [{ scale: scale.value }],
      opacity: opacity.value,
    };
  }) as AnimatedStyle;

  return (
    <MotionView
      style={rippleStyle}
      className={cn(
        'absolute inset-0 rounded-full border-2 pointer-events-none',
        hasUrgent ? 'border-destructive' : 'border-primary'
      )}
      aria-hidden="true"
    >
      <span className="sr-only">Ripple effect</span>
    </MotionView>
  );
}

function UrgentGlow(): JSX.Element {
  const shadowRadius = useSharedValue(0);
  const shadowOpacity = useSharedValue(0);

  useEffect(() => {
    shadowRadius.value = withRepeat(
      withSequence(withTiming(8, { duration: 750 }), withTiming(0, { duration: 750 })),
      -1,
      true
    );
    shadowOpacity.value = withRepeat(
      withSequence(withTiming(0.3, { duration: 750 }), withTiming(0, { duration: 750 })),
      -1,
      true
    );
  }, [shadowRadius, shadowOpacity]);

  const glowStyle = useAnimatedStyle(() => {
    const radius = interpolate(shadowRadius.value, [0, 8], [0, 8], Extrapolation.CLAMP);
    const opacity = interpolate(shadowOpacity.value, [0, 0.3], [0, 0.3], Extrapolation.CLAMP);
    return {
      boxShadow: `0 0 ${radius}px rgba(239, 68, 68, ${opacity})`,
    };
  }) as AnimatedStyle;

  return (
    <MotionView
      style={glowStyle}
      className="absolute inset-0 rounded-full pointer-events-none"
      aria-hidden="true"
    >
      <span className="sr-only">Urgent glow effect</span>
    </MotionView>
  );
}

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
