  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Textarea } from '@/components/ui/textarea';
import { haptics } from '@/lib/haptics';
import { createLogger } from '@/lib/logger';
import type {
  Playdate,
  PlaydateLocation,
  PlaydateStatus,
  PlaydateType,
} from '@/lib/playdate-types';

const logger = createLogger('PlaydateScheduler');
import type { Match, Pet } from '@/lib/types';
import {
  Calendar,
  Check,
  Clock,
  MapPin,
  MapTrifold,
  NavigationArrow,
  Park,
  PawPrint,
  Phone,
  Plus,
  ShareNetwork,
  Umbrella,
  VideoCamera,
  X,
} from '@phosphor-icons/react';
import { differenceInDays, format, isPast } from 'date-fns';
import { Presence, MotionView } from '@petspark/motion';
import { lazy, Suspense, useCallback, useMemo, useState } from 'react';
import { toast } from 'sonner';
import { useKV } from '@/hooks/use-storage';
import { ErrorBoundary } from '@/components/error/ErrorBoundary';

const LocationPicker = lazy(() => import('./LocationPicker'));

interface PlaydateSchedulerProps {
  match: Match;
  userPet: Pet;
  onClose: () => void;
  onStartVideoCall?: () => void;
  onStartVoiceCall?: () => void;
}

export default function PlaydateScheduler({
  match,
  userPet,
  onClose,
  onStartVideoCall,
  onStartVoiceCall,
}: PlaydateSchedulerProps) {
  const [playdates, setPlaydates] = useKV<Playdate[]>('playdates', []);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showLocationPicker, setShowLocationPicker] = useState(false);
  const [selectedLocation, setSelectedLocation] = useState<PlaydateLocation | null>(null);

  const [formData, setFormData] = useState<{
    title: string;
    type: PlaydateType;
    date: string;
    startTime: string;
    endTime: string;
    description: string;
    notes: string;
  }>({
    title: 'Playdate at the Park',
    type: 'park',
    date: new Date().toISOString().split('T')[0]!,
    startTime: '10:00',
    endTime: '11:30',
    description: "Let's meet up for some fun!",
    notes: '',
  });

  // Memoize filtered playdates to avoid recalculation on every render
  const matchPlaydates = useMemo(
    () => (playdates ?? []).filter((p: Playdate) => p.matchId === match.id),
    [playdates, match.id]
  );

  const getPlaydateIcon = (type: PlaydateType) => {
    switch (type) {
      case 'park':
        return <Park size={20} weight="fill" />;
      case 'walk':
        return <Umbrella size={20} />;
      case 'playdate':
        return <PawPrint size={20} weight="fill" />;
      default:
        return <Calendar size={20} />;
    }
  };

  const getStatusColor = (status: PlaydateStatus) => {
    switch (status) {
      case 'confirmed':
        return 'text-green-600 bg-green-100 dark:bg-green-900/20';
      case 'pending':
        return 'text-yellow-600 bg-yellow-100 dark:bg-yellow-900/20';
      case 'completed':
        return 'text-blue-600 bg-blue-100 dark:bg-blue-900/20';
      case 'cancelled':
        return 'text-gray-600 bg-gray-100 dark:bg-gray-900/20';
    }
  };

  const handleCreatePlaydate = useCallback(() => {
    if (!selectedLocation) {
      void toast.error('Please select a location');
      return;
    }

    const playdateDate = formData.date ?? new Date().toISOString().split('T')[0];
    const newPlaydate: Playdate = {
      id: `playdate-${String(Date.now() ?? '')}`,
      matchId: match.id,
      petIds: [userPet.id, match.matchedPetId],
      ownerIds: [userPet.ownerId ?? 'user', match.matchedPetId],
      title: formData.title,
      type: formData.type,
      date: playdateDate,
      startTime: formData.startTime ?? '10:00',
      endTime: formData.endTime ?? '11:30',
      location: selectedLocation,
      description: formData.description,
      status: 'pending',
      createdBy: 'user',
      notes: formData.notes,
      reminderSent: false,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    setPlaydates((current: Playdate[] | undefined) => [...(current ?? []), newPlaydate]);
    void haptics.success();
    void toast.success('Playdate scheduled!', {
      description: `Invitation sent for ${format(new Date(playdateDate), 'MMM dd, yyyy')}`,
    });
    setShowCreateForm(false);
    setSelectedLocation(null);
  }, [
    selectedLocation,
    match.id,
    match.matchedPetId,
    userPet.id,
    userPet.ownerId,
    formData,
    setPlaydates,
  ]);

  const handleLocationChange = useCallback((location: PlaydateLocation) => {
    setSelectedLocation(location);
    setShowLocationPicker(false);
  }, []);

  const handleShareLocation = useCallback((playdate: Playdate) => {
    if (!playdate.location.lat || !playdate.location.lng) {
      void toast.error('Location coordinates not available');
      return;
    }

    const mapsUrl = `https://www.google.com/maps/search/?api=1&query=${playdate.location.lat},${playdate.location.lng}`;

    try {
      if (navigator.share) {
        navigator
          .share({
            title: playdate.title,
            text: `Meet me at ${playdate.location.name}`,
            url: mapsUrl,
          })
          .then(() => {
            void toast.success('Location shared!');
            void haptics.success();
          })
          .catch((error) => {
            // User cancelled or share failed
            if (error instanceof Error && error.name !== 'AbortError') {
              const err = error instanceof Error ? error : new Error(String(error));
              logger.error('PlaydateScheduler shareLocation error', err, {
                playdateId: playdate.id,
              });
              void toast.error('Failed to share location. Please try again.');
            }
          });
      } else {
        navigator.clipboard
          .writeText(mapsUrl)
          .then(() => {
            void toast.success('Location link copied to clipboard!');
            void haptics.success();
          })
          .catch((error) => {
            const err = error instanceof Error ? error : new Error(String(error));
            logger.error('PlaydateScheduler copyToClipboard error', err, {
              playdateId: playdate.id,
            });
            void toast.error('Failed to copy location link. Please try again.');
          });
      }
    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));
      logger.error('PlaydateScheduler shareLocation sync error', err, {
        playdateId: playdate.id,
      });
      void toast.error('Failed to share location. Please try again.');
    }
  }, []);

  const handleGetDirections = useCallback((playdate: Playdate) => {
    if (!playdate.location.lat || !playdate.location.lng) {
      void toast.info('Opening location in maps...');
      const searchUrl = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(
        playdate.location.address
      )}`;
      window.open(searchUrl, '_blank');
      return;
    }

    const directionsUrl = `https://www.google.com/maps/dir/?api=1&destination=${playdate.location.lat},${playdate.location.lng}`;
    window.open(directionsUrl, '_blank');
    haptics.success();
  }, []);

  const handleConfirmPlaydate = useCallback(
    (playdateId: string) => {
      setPlaydates((current: Playdate[] | undefined) =>
        (current ?? []).map((p: Playdate) =>
          p.id === playdateId
            ? { ...p, status: 'confirmed' as PlaydateStatus, updatedAt: new Date().toISOString() }
            : p
        )
      );
      void haptics.success();
      void toast.success('Playdate confirmed!', { description: 'Both parties have confirmed' });
    },
    [setPlaydates]
  );

  const handleCancelPlaydate = useCallback(
    (playdateId: string) => {
      setPlaydates((current: Playdate[] | undefined) =>
        (current ?? []).map((p: Playdate) =>
          p.id === playdateId
            ? { ...p, status: 'cancelled' as PlaydateStatus, updatedAt: new Date().toISOString() }
            : p
        )
      );
      void haptics.light();
      void toast.info('Playdate cancelled', { description: 'The other party has been notified' });
    },
    [setPlaydates]
  );

  return (
    <MotionView
      className="fixed inset-0 bg-background/95 backdrop-blur-sm z-50 overflow-auto"
      initial={{ opacity: 0, y: 12 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: 12 }}
    >
      <div className="max-w-4xl mx-auto p-4 sm:p-6 lg:p-8">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 rounded-full bg-linear-to-br from-primary to-accent flex items-center justify-center">
              <Calendar size={24} className="text-white" weight="fill" />
            </div>
            <div>
              <h1 className="text-2xl font-bold">Playdate Scheduler</h1>
              <p className="text-sm text-muted-foreground">
                Coordinate meetups with {match.matchedPetName}
              </p>
            </div>
          </div>
          <div className="flex items-center gap-2">
            {onStartVideoCall && (
              <Button
                variant="ghost"
                size="icon"
                onClick={onStartVideoCall}
                className="hover:bg-primary/10"
                aria-label="Start video call"
                title="Start video call"
              >
                <VideoCamera size={22} weight="fill" className="text-primary" />
              </Button>
            )}
            {onStartVoiceCall && (
              <Button
                variant="ghost"
                size="icon"
                onClick={onStartVoiceCall}
                className="hover:bg-primary/10"
                aria-label="Start voice call"
                title="Start voice call"
              >
                <Phone size={22} weight="fill" className="text-primary" />
              </Button>
            )}
            <Button
              variant="ghost"
              size="icon"
              onClick={onClose}
              aria-label="Close playdate scheduler"
              title="Close"
            >
              <X size={24} />
            </Button>
          </div>
        </div>

        <Tabs defaultValue="upcoming" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="upcoming">Upcoming</TabsTrigger>
            <TabsTrigger value="history">History</TabsTrigger>
          </TabsList>

          <TabsContent value="upcoming" className="mt-4">
            <Card>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle>Scheduled Playdates</CardTitle>
                    <CardDescription>Your upcoming meetups</CardDescription>
                  </div>
                  <Button onClick={() => { setShowCreateForm(true); }} size="sm">
                    <Plus size={16} className="mr-2" />
                    Schedule Playdate
                  </Button>
                </div>
              </CardHeader>
              <CardContent>
                <ScrollArea className="h-125">
                  <Presence visible={showCreateForm}>
                    {showCreateForm && (
                      <MotionView
                        initial={{ opacity: 0, y: -6 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: -6 }}
                        className="mb-6 p-4 rounded-lg border bg-card/50"
                      >
                        <h3 className="text-lg font-semibold mb-4">Create Playdate</h3>
                        <div className="space-y-4">
                          <div>
                            <Label htmlFor="title">Title</Label>
                            <Input
                              id="title"
                              value={formData.title}
                              onChange={(e) =>
                                setFormData((prev) => ({ ...prev, title: e.target.value }))
                              }
                              placeholder="Playdate at the Park"
                            />
                          </div>

                          <div>
                            <Label htmlFor="type">Type</Label>
                            <Select
                              value={formData.type}
                              onValueChange={(value) =>
                                setFormData((prev) => ({ ...prev, type: value as PlaydateType }))
                              }
                            >
                              <SelectTrigger>
                                <SelectValue />
                              </SelectTrigger>
                              <SelectContent>
                                <SelectItem value="park">Park Visit</SelectItem>
                                <SelectItem value="walk">Walk Together</SelectItem>
                                <SelectItem value="playdate">General Playdate</SelectItem>
                                <SelectItem value="training">Training Session</SelectItem>
                                <SelectItem value="event">Pet Event</SelectItem>
                                <SelectItem value="other">Other</SelectItem>
                              </SelectContent>
                            </Select>
                          </div>

                          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                            <div>
                              <Label htmlFor="date">Date</Label>
                              <Input
                                id="date"
                                type="date"
                                value={formData.date}
                                onChange={(e) =>
                                  setFormData((prev) => ({ ...prev, date: e.target.value }))
                                }
                              />
                            </div>
                            <div>
                              <Label htmlFor="startTime">Start Time</Label>
                              <Input
                                id="startTime"
                                type="time"
                                value={formData.startTime}
                                onChange={(e) =>
                                  setFormData((prev) => ({ ...prev, startTime: e.target.value }))
                                }
                              />
                            </div>
                            <div>
                              <Label htmlFor="endTime">End Time</Label>
                              <Input
                                id="endTime"
                                type="time"
                                value={formData.endTime}
                                onChange={(e) =>
                                  setFormData((prev) => ({ ...prev, endTime: e.target.value }))
                                }
                              />
                            </div>
                          </div>

                          <div>
                            <Label>Location</Label>
                            {selectedLocation ? (
                              <Card className="p-3 bg-accent/5 border-primary/20">
                                <div className="flex items-start justify-between">
                                  <div className="flex items-start gap-2">
                                    <MapPin
                                      size={18}
                                      className="text-primary mt-0.5"
                                      weight="fill"
                                    />
                                    <div>
                                      <p className="font-medium text-sm">{selectedLocation.name}</p>
                                      <p className="text-xs text-muted-foreground">
                                        {selectedLocation.address}
                                      </p>
                                    </div>
                                  </div>
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    onClick={() => { setShowLocationPicker(true); }}
                                  >
                                    Change
                                  </Button>
                                </div>
                              </Card>
                            ) : (
                              <Button
                                variant="outline"
                                className="w-full justify-start"
                                onClick={() => { setShowLocationPicker(true); }}
                              >
                                <MapTrifold size={18} className="mr-2" />
                                Choose Location
                              </Button>
                            )}
                          </div>

                          <div>
                            <Label htmlFor="description">Description</Label>
                            <Textarea
                              id="description"
                              value={formData.description}
                              onChange={(e) =>
                                setFormData((prev) => ({ ...prev, description: e.target.value }))
                              }
                              placeholder="Let's meet up for some fun!"
                              rows={3}
                            />
                          </div>

                          <div className="flex gap-2">
                            <Button onClick={handleCreatePlaydate} className="flex-1">
                              <Check size={16} className="mr-2" />
                              Schedule Playdate
                            </Button>
                            <Button
                              variant="outline"
                              onClick={() => { setShowCreateForm(false); }}
                              className="flex-1"
                            >
                              Cancel
                            </Button>
                          </div>
                        </div>
                      </MotionView>
                    )}
                  </Presence>

                  {matchPlaydates.filter(
                    (p) => p.status !== 'completed' && p.status !== 'cancelled'
                  ).length === 0 ? (
                    <div className="text-center py-12">
                      <Calendar size={48} className="mx-auto text-muted-foreground mb-3" />
                      <p className="text-muted-foreground">No upcoming playdates</p>
                      <p className="text-sm text-muted-foreground mt-1">
                        Schedule one to meet up!
                      </p>
                    </div>
                  ) : (
                    <div className="space-y-3">
                      {matchPlaydates
                        .filter((p) => p.status !== 'completed' && p.status !== 'cancelled')
                        .sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime())
                        .map((playdate, index) => {
                          const daysUntil = differenceInDays(new Date(playdate.date), new Date());
                          const isPastDate = isPast(new Date(playdate.date));

                          return (
                            <MotionView
                              key={playdate.id}
                              className="p-4 rounded-lg border bg-card hover:shadow-md transition-shadow"
                              initial={{ opacity: 0, y: 12 }}
                              animate={{ opacity: 1, y: 0 }}
                              transition={{ delay: index * 0.05, type: 'spring', stiffness: 120, damping: 18 }}
                            >
                              <div className="flex items-start justify-between mb-3">
                                <div className="flex items-start gap-3">
                                  <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center shrink-0">
                                    {getPlaydateIcon(playdate.type)}
                                  </div>
                                  <div>
                                    <h4 className="font-semibold">{playdate.title}</h4>
                                    <div className="flex items-center gap-2 text-sm text-muted-foreground mt-1">
                                      <Calendar size={14} />
                                      {format(new Date(playdate.date), 'EEE, MMM dd, yyyy')}
                                      {!isPastDate &&
                                        (daysUntil === 0
                                          ? '  Today!'
                                          : daysUntil === 1
                                            ? '  Tomorrow'
                                            : `  ${daysUntil} days away`)}
                                    </div>
                                    <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                      <Clock size={14} />
                                      {playdate.startTime} - {playdate.endTime}
                                    </div>
                                  </div>
                                </div>
                                <Badge className={getStatusColor(playdate.status)}>
                                  {playdate.status}
                                </Badge>
                              </div>

                              <div className="flex items-start gap-2 text-sm mb-3">
                                <MapPin
                                  size={16}
                                  className="text-muted-foreground mt-0.5 shrink-0"
                                />
                                <div>
                                  <p className="font-medium">{playdate.location.name}</p>
                                  <p className="text-muted-foreground">
                                    {playdate.location.address}
                                  </p>
                                </div>
                              </div>

                              {playdate.description && (
                                <p className="text-sm text-muted-foreground mb-3">
                                  {playdate.description}
                                </p>
                              )}

                              <div className="flex flex-col gap-2">
                                <div className="flex gap-2">
                                  {playdate.status === 'pending' && (
                                    <Button
                                      size="sm"
                                      onClick={() => { handleConfirmPlaydate(playdate.id); }}
                                      className="flex-1"
                                    >
                                      <Check size={16} className="mr-2" />
                                      Confirm
                                    </Button>
                                  )}
                                  {playdate.status !== 'cancelled' && (
                                    <Button
                                      size="sm"
                                      variant="outline"
                                      onClick={() => { handleCancelPlaydate(playdate.id); }}
                                      className="flex-1"
                                    >
                                      <X size={16} className="mr-2" />
                                      Cancel
                                    </Button>
                                  )}
                                </div>
                                <div className="flex gap-2">
                                  <Button
                                    size="sm"
                                    variant="secondary"
                                    onClick={() => { handleGetDirections(playdate); }}
                                    className="flex-1"
                                  >
                                    <NavigationArrow size={16} className="mr-2" weight="bold" />
                                    Directions
                                  </Button>
                                  <Button
                                    size="sm"
                                    variant="secondary"
                                    onClick={() => { handleShareLocation(playdate); }}
                                    className="flex-1"
                                  >
                                    <ShareNetwork size={16} className="mr-2" weight="bold" />
                                    Share
                                  </Button>
                                </div>
                              </div>
                            </MotionView>
                          );
                        })}
                    </div>
                  )}
                </ScrollArea>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="history" className="mt-4">
            <Card>
              <CardHeader>
                <CardTitle>Past Playdates</CardTitle>
                <CardDescription>Your meetup history</CardDescription>
              </CardHeader>
              <CardContent>
                <ScrollArea className="h-125">
                  {matchPlaydates.filter(
                    (p) => p.status === 'completed' || p.status === 'cancelled'
                  ).length === 0 ? (
                    <div className="text-center py-12">
                      <Calendar size={48} className="mx-auto text-muted-foreground mb-3" />
                      <p className="text-muted-foreground">No past playdates</p>
                    </div>
                  ) : (
                    <div className="space-y-3">
                      {matchPlaydates
                        .filter((p) => p.status === 'completed' || p.status === 'cancelled')
                        .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime())
                        .map((playdate, index) => (
                          <MotionView
                            key={playdate.id}
                            className="p-4 rounded-lg border bg-card opacity-75"
                            initial={{ opacity: 0, y: 10 }}
                            animate={{ opacity: 0.9, y: 0 }}
                            transition={{ delay: index * 0.04, type: 'spring', stiffness: 110, damping: 16 }}
                          >
                            <div className="flex items-start justify-between mb-2">
                              <div className="flex items-start gap-3">
                                <div className="w-10 h-10 rounded-full bg-muted flex items-center justify-center shrink-0">
                                  {getPlaydateIcon(playdate.type)}
                                </div>
                                <div>
                                  <h4 className="font-semibold">{playdate.title}</h4>
                                  <p className="text-sm text-muted-foreground">
                                    {format(new Date(playdate.date), 'MMM dd, yyyy')} {' '}
                                    {playdate.startTime}
                                  </p>
                                </div>
                              </div>
                              <Badge className={getStatusColor(playdate.status)}>
                                {playdate.status}
                              </Badge>
                            </div>
                            <div className="flex items-center gap-2 text-sm text-muted-foreground">
                              <MapPin size={14} />
                              {playdate.location.name}
                            </div>
                          </MotionView>
                        ))}
                    </div>
                  )}
                </ScrollArea>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>

      {showLocationPicker && (
        <ErrorBoundary
          fallback={
            <div className="fixed inset-0 bg-background/80 backdrop-blur-sm z-50 flex items-center justify-center">
              <Card className="p-6">
                <div className="text-center">
                  <p className="text-destructive mb-2">Failed to load location picker</p>
                  <Button onClick={() => { setShowLocationPicker(false); }}>Close</Button>
                </div>
              </Card>
            </div>
          }
        >
          <Suspense
            fallback={
              <div className="fixed inset-0 bg-background/80 backdrop-blur-sm z-50 flex items-center justify-center">
                <div className="text-center">
                  <MotionView
                    animate={{ rotate: 360 }}
                    transition={{ duration: 1, repeat: Infinity, ease: 'linear' }}
                    className="w-12 h-12 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4"
                  />
                  <p className="text-muted-foreground">Loading map...</p>
                </div>
              </div>
            }
          >
            <LocationPicker
              {...(selectedLocation !== null ? { value: selectedLocation } : {})}
              onChange={handleLocationChange}
              onClose={() => { setShowLocationPicker(false); }}
            />
          </Suspense>
        </ErrorBoundary>
      )}
    </MotionView>
  );
}

import { useState, useEffect } from 'react';
import { useStorage } from '@/hooks/use-storage';
import { MotionView } from '@petspark/motion';
import { Plus, Check } from '@phosphor-icons/react';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { isTruthy } from '@petspark/shared';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import type { Story, StoryHighlight } from '@petspark/shared';
import type { Pet } from '@/lib/types';
import { createStoryHighlight } from '@/lib/stories-utils';
import { filterActiveStories } from '@/lib/stories-utils';
import { haptics } from '@/lib/haptics';
import { toast } from 'sonner';

interface CreateHighlightDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  existingHighlight?: StoryHighlight;
}

export default function CreateHighlightDialog({
  open,
  onOpenChange,
  existingHighlight,
}: CreateHighlightDialogProps) {
  const [stories] = useStorage<Story[]>('stories', []);
  const [userPets] = useStorage<Pet[]>('user-pets', []);
  const [currentUser] = useStorage<{ id: string; name: string }>('current-user', {
    id: 'user-1',
    name: 'User',
  });
  const [, setHighlights] = useStorage<StoryHighlight[]>('story-highlights', []);

  const [title, setTitle] = useState(existingHighlight?.title ?? '');
  const [selectedStories, setSelectedStories] = useState<Set<string>>(
    new Set(existingHighlight?.stories.map((s) => s.id) ?? [])
  );
  const [coverImageUrl, setCoverImageUrl] = useState(existingHighlight?.coverImage ?? '');

  const myStories = (stories ?? []).filter((s) => s.userId === (currentUser?.id ?? 'user-1'));

  const userStories = filterActiveStories(myStories);

  useEffect(() => {
    if (selectedStories.size > 0 && !coverImageUrl) {
      const firstSelected = userStories.find((s) => selectedStories.has(s.id));
      if (firstSelected) {
        setCoverImageUrl(firstSelected.thumbnailUrl ?? firstSelected.mediaUrl);
      }
    }
  }, [selectedStories, userStories, coverImageUrl]);

  const handleToggleStory = (storyId: string) => {
    void haptics.trigger('selection');
    setSelectedStories((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(storyId)) {
        newSet.delete(storyId);
      } else {
        newSet.add(storyId);
      }
      return newSet;
    });
  };

  const handleSetCover = (story: Story) => {
    void haptics.trigger('light');
    setCoverImageUrl(story.thumbnailUrl ?? story.mediaUrl);
    void toast.success('Cover image set', { duration: 1500 });
  };

  const handleSave = () => {
    if (!title.trim()) {
      void toast.error('Please enter a title');
      return;
    }

    if (selectedStories.size === 0) {
      void toast.error('Please select at least one story');
      return;
    }

    void haptics.trigger('success');

    const selectedStoryObjects = userStories.filter((s) => selectedStories.has(s.id));
    const firstPet = userPets?.[0];

    if (isTruthy(existingHighlight)) {
      setHighlights((current) =>
        (current ?? []).map((h) =>
          h.id === existingHighlight.id
            ? {
              ...h,
              title,
              coverImage: coverImageUrl,
              stories: selectedStoryObjects,
              updatedAt: new Date().toISOString(),
            }
            : h
        )
      );
      void toast.success('Highlight updated!');
    } else {
      const newHighlight = createStoryHighlight(
        currentUser?.id ?? 'user-1',
        firstPet?.id ?? 'pet-1',
        title,
        coverImageUrl,
        selectedStoryObjects
      );

      setHighlights((current) => [...(current ?? []), newHighlight]);
      void toast.success('Highlight created!');
    }

    onOpenChange(false);
    resetForm();
  };

  const resetForm = () => {
    setTitle('');
    setSelectedStories(new Set());
    setCoverImageUrl('');
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-hidden flex flex-col">
        <DialogHeader>
          <DialogTitle>{existingHighlight ? 'Edit Highlight' : 'Create Highlight'}</DialogTitle>
          <DialogDescription>
            Save your favorite stories to a permanent highlight collection
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 overflow-y-auto flex-1 pr-2">
          <div className="space-y-2">
            <Label htmlFor="highlight-title">Highlight Title</Label>
            <Input
              id="highlight-title"
              value={title}
              onChange={(e) => { setTitle(e.target.value); }}
              placeholder="e.g., Summer Adventures, Best Moments"
              maxLength={30}
            />
            <p className="text-xs text-muted-foreground">{title.length}/30 characters</p>
          </div>

          {userStories.length === 0 ? (
            <div className="py-12 text-center">
              <p className="text-muted-foreground mb-4">You don't have any active stories yet</p>
              <p className="text-sm text-muted-foreground">
                Create some stories first to add them to highlights
              </p>
            </div>
          ) : (
            <>
              <div>
                <Label className="mb-3 block">Select Stories ({selectedStories.size})</Label>
                <div className="grid grid-cols-3 gap-3">
                  {userStories.map((story, index) => {
                    const isSelected = selectedStories.has(story.id);
                    const isCover = coverImageUrl === (story.thumbnailUrl ?? story.mediaUrl);

                    return (
                      <MotionView
                        key={story.id}
                        initial={{ opacity: 0, scale: 0.8 }}
                        animate={{ opacity: 1, scale: 1 }}
                        transition={{ delay: index * 0.03 }}
                        className="relative"
                      >
                        <button
                          onClick={() => { handleToggleStory(story.id); }}
                          className={`aspect-9/16 rounded-2xl overflow-hidden relative group w-full border-2 transition-all ${String(isSelected
                            ? 'border-primary shadow-lg'
                            : 'border-transparent hover:border-border')
                            }`}
                        >
                          <img
                            src={story.thumbnailUrl ?? story.mediaUrl}
                            alt={story.caption ?? 'Story'}
                            className="w-full h-full object-cover"
                          />

                          <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition-opacity" />

                          {isSelected && (
                            <MotionView
                              initial={{ scale: 0 }}
                              animate={{ scale: 1 }}
                              className="absolute top-2 right-2 w-6 h-6 bg-primary rounded-full flex items-center justify-center shadow-lg"
                            >
                              <Check size={16} weight="bold" className="text-white" />
                            </MotionView>
                          )}

                          {isCover && (
                            <div className="absolute bottom-2 left-2 right-2">
                              <div className="bg-primary/90 backdrop-blur-sm rounded-lg px-2 py-1">
                                <p className="text-white text-xs font-semibold text-center">
                                  Cover
                                </p>
                              </div>
                            </div>
                          )}
                        </button>

                        {isSelected && !isCover && (
                          <Button
                            onClick={() => { handleSetCover(story); }}
                            variant="secondary"
                            size="sm"
                            className="absolute bottom-2 left-2 right-2 text-xs h-7"
                          >
                            Set as Cover
                          </Button>
                        )}
                      </MotionView>
                    );
                  })}
                </div>
              </div>
            </>
          )}
        </div>

        <DialogFooter>
          <Button variant="outline" onClick={() => { onOpenChange(false); }}>
            Cancel
          </Button>
          <Button
            onClick={handleSave}
            disabled={!title.trim() || selectedStories.size === 0}
            className="bg-linear-to-r from-primary to-accent hover:from-primary/90 hover:to-accent/90"
          >
            <Plus size={18} weight="bold" className="mr-2" />
            {existingHighlight ? 'Update' : 'Create'} Highlight
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from 'react';
import { useStorage } from '@/hooks/use-storage';
import { motion } from '@petspark/motion';
import { Plus, Check, BookmarkSimple } from '@phosphor-icons/react';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import type { Story, StoryHighlight } from '@petspark/shared';
import type { Pet } from '@/lib/types';
import { createStoryHighlight } from '@/lib/stories-utils';
import { haptics } from '@/lib/haptics';
import { toast } from 'sonner';

interface SaveToHighlightDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  story: Story;
  onSaved?: () => void;
}

export default function SaveToHighlightDialog({
  open,
  onOpenChange,
  story,
  onSaved,
}: SaveToHighlightDialogProps) {
  const [highlights, setHighlights] = useStorage<StoryHighlight[]>('story-highlights', []);
  const [userPets] = useStorage<Pet[]>('user-pets', []);

  const [showNewHighlight, setShowNewHighlight] = useState(false);
  const [newHighlightTitle, setNewHighlightTitle] = useState('');
  const [selectedHighlightId, setSelectedHighlightId] = useState<string | null>(null);

  const userHighlights = (highlights ?? []).filter((h) => h.userId === story.userId);
  const storyAlreadyInHighlight = (highlightId: string) => {
    const highlight = userHighlights.find((h) => h.id === highlightId);
    return highlight?.stories.some((s) => s.id === story.id) ?? false;
  };

  const handleSelectHighlight = (highlightId: string) => {
    if (storyAlreadyInHighlight(highlightId)) {
      void toast.error('Story already in this highlight');
      return;
    }
    void haptics.trigger('selection');
    setSelectedHighlightId(highlightId);
  };

  const handleSaveToExisting = () => {
    if (!selectedHighlightId) return;

    void haptics.trigger('success');

    setHighlights((current) =>
      (current ?? []).map((h) =>
        h.id === selectedHighlightId
          ? {
            ...h,
            stories: [...h.stories, story],
            updatedAt: new Date().toISOString(),
          }
          : h
      )
    );

    void toast.success('Story saved to highlight!', {
      duration: 2000,
      description: 'You can view it anytime in your highlights',
    });

    onSaved?.();
    onOpenChange(false);
    resetState();
  };

  const handleCreateNew = () => {
    if (!newHighlightTitle.trim()) {
      void toast.error('Please enter a highlight title');
      return;
    }

    void haptics.trigger('success');

    const firstPet = userPets?.[0];
    const newHighlight = createStoryHighlight(
      story.userId,
      firstPet?.id ?? story.petId,
      newHighlightTitle,
      story.thumbnailUrl ?? story.mediaUrl,
      [story]
    );

    setHighlights((current) => [...(current ?? []), newHighlight]);

    void toast.success('Highlight created!', {
      duration: 2000,
      description: `"${newHighlightTitle}" has been created with this story`,
    });

    onSaved?.();
    onOpenChange(false);
    resetState();
  };

  const resetState = () => {
    setShowNewHighlight(false);
    setNewHighlightTitle('');
    setSelectedHighlightId(null);
  };

  const handleOpenChange = (open: boolean) => {
    if (!open) {
      resetState();
    }
    onOpenChange(open);
  };

  return (
    <Dialog open={open} onOpenChange={handleOpenChange}>
      <DialogContent className="max-w-md max-h-[80vh] overflow-hidden flex flex-col">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <BookmarkSimple size={24} weight="fill" className="text-primary" />
            Save to Highlight
          </DialogTitle>
          <DialogDescription>Save this story to a permanent highlight collection</DialogDescription>
        </DialogHeader>

        <div className="flex-1 overflow-hidden">
          {showNewHighlight ? (
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              className="space-y-4 py-4"
            >
              <div className="space-y-2">
                <Label htmlFor="new-highlight-title">Highlight Name</Label>
                <Input
                  id="new-highlight-title"
                  value={newHighlightTitle}
                  onChange={(e) => { setNewHighlightTitle(e.target.value); }}
                  placeholder="e.g., Summer Adventures, Best Moments"
                  maxLength={30}
                  autoFocus
                />
                <p className="text-xs text-muted-foreground">
                  {newHighlightTitle.length}/30 characters
                </p>
              </div>

              <div className="aspect-9/16 max-h-48 rounded-2xl overflow-hidden bg-muted">
                <img
                  src={story.thumbnailUrl ?? story.mediaUrl}
                  alt="Story preview"
                  className="w-full h-full object-cover"
                />
              </div>
            </motion.div>
          ) : (
            <ScrollArea className="h-100 pr-4">
              <div className="space-y-3 py-4">
                {userHighlights.length === 0 ? (
                  <div className="text-center py-12">
                    <BookmarkSimple size={48} className="mx-auto text-muted-foreground mb-3" />
                    <p className="text-muted-foreground mb-2">No highlights yet</p>
                    <p className="text-sm text-muted-foreground">
                      Create your first highlight to save this story
                    </p>
                  </div>
                ) : (
                  userHighlights.map((highlight) => {
                    const alreadyInHighlight = storyAlreadyInHighlight(highlight.id);
                    const isSelected = selectedHighlightId === highlight.id;

                    return (
                      <motion.button
                        key={highlight.id}
                        onClick={() => !alreadyInHighlight && handleSelectHighlight(highlight.id)}
                        disabled={alreadyInHighlight}
                        className={`w-full p-4 rounded-2xl flex items-center gap-4 transition-all ${alreadyInHighlight
                          ? 'opacity-50 cursor-not-allowed bg-muted'
                          : isSelected
                            ? 'bg-primary/20 border-2 border-primary'
                            : 'glass-effect hover:bg-white/20 dark:hover:bg-white/5'
                          }`}
                        whileHover={!alreadyInHighlight ? { scale: 1.02 } : {}}
                        whileTap={!alreadyInHighlight ? { scale: 0.98 } : {}}
                      >
                        <Avatar className="w-16 h-16 ring-2 ring-border">
                          <AvatarImage src={highlight.coverImage} alt={highlight.title} />
                          <AvatarFallback className="bg-linear-to-br from-primary to-accent text-white font-bold">
                            {highlight.title?.[0]?.toUpperCase() ?? '?'}
                          </AvatarFallback>
                        </Avatar>

                        <div className="flex-1 text-left min-w-0">
                          <p className="font-semibold truncate">{highlight.title}</p>
                          <p className="text-sm text-muted-foreground">
                            {highlight.stories?.length ?? 0}{' '}
                            {highlight.stories?.length === 1 ? 'story' : 'stories'}
                          </p>
                          {alreadyInHighlight && (
                            <p className="text-xs text-muted-foreground mt-1">Already added</p>
                          )}
                        </div>

                        {isSelected && !alreadyInHighlight && (
                          <div className="shrink-0 w-8 h-8 rounded-full bg-primary flex items-center justify-center">
                            <Check size={18} weight="bold" className="text-white" />
                          </div>
                        )}

                        {highlight.isPinned && (
                          <div className="shrink-0 text-lg" aria-label="Pinned">

                          </div>
                        )}
                      </motion.button>
                    );
                  })
                )}
              </div>
            </ScrollArea>
          )}
        </div>

        <DialogFooter className="border-t pt-4">
          {showNewHighlight ? (
            <>
              <Button variant="outline" onClick={() => setShowNewHighlight(false)}>
                Back
              </Button>
              <Button
                onClick={handleCreateNew}
                disabled={!newHighlightTitle.trim()}
                className="bg-linear-to-r from-primary to-accent hover:from-primary/90 hover:to-accent/90"
              >
                <Plus size={18} weight="bold" className="mr-2" />
                Create Highlight
              </Button>
            </>
          ) : (
            <>
              <Button
                variant="outline"
                onClick={() => { setShowNewHighlight(true); }}
                className="flex-1"
              >
                <Plus size={18} weight="bold" className="mr-2" />
                New Highlight
              </Button>
              <Button
                onClick={handleSaveToExisting}
                disabled={!selectedHighlightId}
                className="flex-1 bg-linear-to-r from-primary to-accent hover:from-primary/90 hover:to-accent/90"
              >
                <BookmarkSimple size={18} weight="fill" className="mr-2" />
                Save Story
              </Button>
            </>
          )}
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import { Input } from '@/components/ui/input';
import { useFullscreen } from '@/hooks/use-fullscreen';
import { useStoryAnalytics } from '@/hooks/use-story-analytics';
import { useStoryGestures } from '@/hooks/use-story-gestures';
import { haptics } from '@/lib/haptics';
import type { Story } from '@petspark/shared';
import { STORY_REACTION_EMOJIS } from '@petspark/shared';
import { addStoryView, formatStoryTime } from '@/lib/stories-utils';
import { usePrefersReducedMotion } from '@/utils/reduced-motion';
import {
  ArrowsIn,
  ArrowsOut,
  BookmarkSimple,
  DotsThree,
  Heart,
  PaperPlaneRight,
  Pause,
  Play,
  SpeakerHigh,
  SpeakerSlash,
  X,
} from '@phosphor-icons/react';
import { AnimatePresence } from '@/effects/reanimated/animate-presence';
import { useMotionVariants, useHoverLift, useBounceOnTap } from '@/effects/reanimated';
import * as Reanimated from '@petspark/motion';
import { MotionView, type MotionStyle } from '@petspark/motion';
import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { toast } from 'sonner';
import SaveToHighlightDialog from './SaveToHighlightDialog';
import { ProgressiveImage } from '@/components/enhanced/ProgressiveImage';

interface StoryViewerProps {
  stories: Story[];
  initialIndex?: number;
  currentUserId: string;
  currentUserName: string;
  currentUserAvatar?: string;
  onClose: () => void;
  onComplete?: () => void;
  onStoryUpdate?: (story: Story) => void;
}

export default function StoryViewer({
  stories,
  initialIndex = 0,
  currentUserId,
  currentUserName,
  currentUserAvatar,
  onClose,
  onComplete,
  onStoryUpdate,
}: StoryViewerProps) {
  const [currentIndex, setCurrentIndex] = useState(initialIndex);
  const [isPaused, setIsPaused] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [progress, setProgress] = useState(0);
  const [replyText, setReplyText] = useState('');
  const [showReactions, setShowReactions] = useState(false);
  const [showSaveDialog, setShowSaveDialog] = useState(false);

  const startTimeRef = useRef<number>(Date.now());
  const progressIntervalRef = useRef<number | null>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const mediaContainerRef = useRef<HTMLDivElement>(null);
  const _swipeProgress = Reanimated.useSharedValue(0);

  const currentStory = stories[currentIndex];
  const isOwn = currentStory?.userId === currentUserId;
  const prefersReducedMotion = usePrefersReducedMotion();
  const { isFullscreen, toggleFullscreen } = useFullscreen();

  const { trackReaction, trackInteraction } = useStoryAnalytics({
    story: currentStory ?? null,
    currentUserId,
    isActive: !isPaused,
  });

  const handleNext = useCallback(() => {
    const viewDuration = (Date.now() - startTimeRef.current) / 1000;
    const completedView = viewDuration >= (currentStory?.duration ?? 5) * 0.8;

    if (currentStory && !isOwn) {
      const updatedStory = addStoryView(
        currentStory,
        currentUserId,
        currentUserName,
        viewDuration,
        completedView,
        currentUserAvatar ?? undefined
      );
      onStoryUpdate?.(updatedStory);
    }

    trackInteraction('skip');

    if (currentIndex < stories.length - 1) {
      setCurrentIndex((prev) => prev + 1);
      setProgress(0);
    } else {
      onComplete?.();
      onClose();
    }
  }, [
    currentStory,
    currentUserId,
    currentUserName,
    currentUserAvatar,
    isOwn,
    currentIndex,
    stories.length,
    onStoryUpdate,
    onComplete,
    onClose,
    trackInteraction,
  ]);

  const handlePrevious = useCallback(() => {
    if (currentIndex > 0) {
      setCurrentIndex((prev) => prev - 1);
      setProgress(0);
    }
  }, [currentIndex]);

  const handlePauseToggle = useCallback(() => {
    haptics.trigger('selection');
    setIsPaused((prev) => !prev);
  }, []);

  const handleMuteToggle = useCallback(() => {
    haptics.trigger('selection');
    setIsMuted((prev) => !prev);
  }, []);

  const {
    gestureState,
    handlers: gestureHandlers,
    reset: resetGestures,
  } = useStoryGestures({
    onSwipeLeft: handleNext,
    onSwipeRight: handlePrevious,
    onTap: handlePauseToggle,
    onLongPress: () => {
      if (!isOwn) {
        setShowReactions(true);
      }
    },
    onPinchZoom: (scale: number) => {
      if (mediaContainerRef.current) {
        mediaContainerRef.current.style.transform = `scale(${scale})`;
      }
    },
    enablePinchZoom: currentStory?.type === 'photo',
    swipeThreshold: 50,
  });

  const _mediaContainerOpacity = Reanimated.useMotionValue(1);
  const mediaContainerStyle = Reanimated.useAnimatedStyle(() => {
    const opacity = gestureState.isSwiping ? 0.5 : 1;
    const scale = gestureState.isSwiping ? 0.95 : gestureState.pinchScale;
    return {
      opacity,
      scale,
    };
  });

  const swipeOpacityStyle: MotionStyle = {
    opacity: Reanimated.useMotionValue(1),
  };

  const swipeScaleStyle: MotionStyle = {
    scale: Reanimated.useMotionValue(1),
  };

  const startProgress = useCallback(() => {
    if (progressIntervalRef.current) {
      clearInterval(progressIntervalRef.current);
    }

    const duration = currentStory?.duration ?? 5;
    const interval = 50;

    progressIntervalRef.current = window.setInterval(() => {
      setProgress((prev) => {
        const newProgress = prev + (interval / (duration * 1000)) * 100;

        if (newProgress >= 100) {
          handleNext();
          return 0;
        }

        return newProgress;
      });
    }, interval);
  }, [currentStory, handleNext]);

  useEffect(() => {
    startTimeRef.current = Date.now();
    setProgress(0);
    resetGestures();

    if (mediaContainerRef.current) {
      mediaContainerRef.current.style.transform = 'scale(1)';
    }

    if (!isPaused) {
      startProgress();
    }

    return () => {
      if (progressIntervalRef.current) {
        clearInterval(progressIntervalRef.current);
      }
    };
  }, [currentIndex, isPaused, resetGestures, startProgress]);

  useEffect(() => {
    if (currentStory?.type === 'video' && videoRef.current) {
      if (isPaused) {
        videoRef.current.pause();
        trackInteraction('pause');
      } else {
        videoRef.current.play().catch(() => {
          // Video play failed
        });
      }
      videoRef.current.muted = isMuted;
    }
  }, [isPaused, isMuted, currentStory, trackInteraction]);

  useEffect(() => {
    startTimeRef.current = Date.now();
    setProgress(0);
    resetGestures();

    if (mediaContainerRef.current) {
      mediaContainerRef.current.style.transform = 'scale(1)';
    }

    if (!isPaused) {
      startProgress();
    }

    return () => {
      if (progressIntervalRef.current) {
        clearInterval(progressIntervalRef.current);
      }
    };
  }, [currentIndex, isPaused, resetGestures, startProgress]);

  useEffect(() => {
    if (currentStory?.type === 'video' && videoRef.current) {
      if (isPaused) {
        videoRef.current.pause();
        trackInteraction('pause');
      } else {
        videoRef.current.play().catch(() => {
          // Video play failed
        });
      }
      videoRef.current.muted = isMuted;
    }
  }, [isPaused, isMuted, currentStory, trackInteraction]);

  const handleReaction = useCallback(
    (emoji: string) => {
      haptics.trigger('success');

      if (currentStory && !isOwn) {
        trackReaction(emoji);
        const updatedStory = {
          ...currentStory,
          reactions: [
            ...currentStory.reactions,
            {
              emoji,
              userId: currentUserId,
              userName: currentUserName,
              ...(currentUserAvatar !== undefined ? { userAvatar: currentUserAvatar } : {}),
              timestamp: new Date().toISOString(),
            },
          ],
        };
        onStoryUpdate?.(updatedStory);
      }

      void toast.success(`Reacted with ${emoji}`, {
        duration: 1500,
        position: 'top-center',
      });

      setShowReactions(false);
    },
    [
      currentStory,
      isOwn,
      currentUserId,
      currentUserName,
      currentUserAvatar,
      trackReaction,
      onStoryUpdate,
    ]
  );

  const handleReply = useCallback(() => {
    if (!replyText.trim()) return;

    haptics.trigger('light');
    trackInteraction('reply');

    void toast.success('Reply sent!', {
      duration: 2000,
      position: 'top-center',
    });

    setReplyText('');
  }, [replyText, trackInteraction]);

  const handleSaveStory = useCallback(() => {
    if (currentStory) {
      haptics.trigger('selection');
      setShowSaveDialog(true);
    }
  }, [currentStory]);

  const handleShare = useCallback(() => {
    if (currentStory) {
      trackInteraction('share');
      if (navigator.share) {
        navigator
          .share({
            title: `Story by ${currentStory.userName}`,
            text: currentStory.caption ?? '',
            url: `${window.location.origin}/stories/${currentStory.id}`,
          })
          .catch(() => {
            // Share cancelled
          });
      } else {
        void navigator.clipboard.writeText(
          `${window.location.origin}/stories/${currentStory.id}`
        );
        void toast.success('Link copied to clipboard');
      }
    }
  }, [currentStory, trackInteraction]);

  const transitionConfig = prefersReducedMotion
    ? { duration: 0 }
    : { duration: 0.3, ease: [0.4, 0, 0.2, 1] as number[] };

  // Animation hooks for story viewer
  const viewerEntry = useMotionVariants({
    initial: { opacity: 0 },
    animate: { opacity: 1 },
    transition: transitionConfig,
  });

  const reactionButtonHover = useHoverLift({ scale: 1.3 });
  const reactionButtonTap = useBounceOnTap({ scale: 0.9 });

  const captionAnimation = useMotionVariants({
    initial: { opacity: 0, translateY: 20 },
    animate: { opacity: 1, translateY: 0 },
    transition: transitionConfig,
  });

  const reactionsAnimation = useMotionVariants({
    initial: { opacity: 0, translateY: 20 },
    animate: { opacity: 1, translateY: 0 },
    transition: transitionConfig,
  });

  const analyticsAnimation = useMotionVariants({
    initial: { opacity: 0, translateY: 20 },
    animate: { opacity: 1, translateY: 0 },
    transition: transitionConfig,
  });

  const imageEntry = useMotionVariants({
    initial: { scale: prefersReducedMotion ? 1 : 1.1, opacity: 0 },
    animate: { scale: gestureState.pinchScale, opacity: 1 },
    transition: transitionConfig,
  });

  const viewerEntryStyle = useMemo(
    () => ({
      opacity: viewerEntry.opacity,
    }),
    [viewerEntry.opacity]
  );

  const captionStyle = useMemo(
    () => ({
      opacity: captionAnimation.opacity,
      y: captionAnimation.translateY,
    }),
    [captionAnimation.opacity, captionAnimation.translateY]
  );

  const reactionsStyle = useMemo(
    () => ({
      opacity: reactionsAnimation.opacity,
      y: reactionsAnimation.translateY,
    }),
    [reactionsAnimation.opacity, reactionsAnimation.translateY]
  );

  const analyticsStyle = useMemo(
    () => ({
      opacity: analyticsAnimation.opacity,
      y: analyticsAnimation.translateY,
    }),
    [analyticsAnimation.opacity, analyticsAnimation.translateY]
  );

  const imageEntryStyle = useMemo(
    () => ({
      opacity: imageEntry.opacity,
      scale: imageEntry.scale,
    }),
    [imageEntry.opacity, imageEntry.scale]
  );

  if (!currentStory) return null;

  return (
    <MotionView
      style={viewerEntryStyle}
      className="fixed inset-0 z-100 bg-black"
      role="dialog"
      aria-modal="true"
      aria-label="Story viewer"
      {...gestureHandlers}
    >
      <div className="relative w-full h-full flex items-center justify-center">
        {/* Progress bars */}
        <div
          className="absolute top-0 left-0 right-0 z-20 p-4 space-y-3"
          aria-label="Story progress"
        >
          <div className="flex gap-1">
            {stories.map((_, idx) => (
              <div
                key={idx}
                className="flex-1 h-1 bg-white/30 rounded-full overflow-hidden"
                role="progressbar"
                aria-valuenow={idx === currentIndex ? progress : idx < currentIndex ? 100 : 0}
                aria-valuemin={0}
                aria-valuemax={100}
                aria-label={`Story ${String(idx + 1)} of ${String(stories.length)}`}
              >
                <MotionView
                  className="h-full bg-white"
                  style={{
                    width:
                      idx < currentIndex ? '100%' : idx === currentIndex ? `${progress}%` : '0%',
                    transition: prefersReducedMotion ? 'none' : 'width 0.1s linear',
                  }}
                />
              </div>
            ))}
          </div>

          {/* Header */}
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <Avatar className="w-10 h-10 ring-2 ring-white">
                <AvatarImage src={currentStory.userAvatar} alt={currentStory.userName} />
                <AvatarFallback className="bg-linear-to-br from-primary to-accent text-white font-bold">
                  {currentStory.userName[0]}
                </AvatarFallback>
              </Avatar>
              <div>
                <p className="text-white font-semibold text-sm">{currentStory.petName}</p>
                <p className="text-white/80 text-xs">{formatStoryTime(currentStory.createdAt)}</p>
              </div>
            </div>

            <div className="flex items-center gap-2">
              <Button
                variant="ghost"
                size="icon"
                onClick={handlePauseToggle}
                className="text-white hover:bg-white/20"
                aria-label={isPaused ? 'Play story' : 'Pause story'}
              >
                {isPaused ? <Play size={20} weight="fill" /> : <Pause size={20} weight="fill" />}
              </Button>

              {currentStory.type === 'video' && (
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={handleMuteToggle}
                  className="text-white hover:bg-white/20"
                  aria-label={isMuted ? 'Unmute video' : 'Mute video'}
                >
                  {isMuted ? (
                    <SpeakerSlash size={20} weight="fill" />
                  ) : (
                    <SpeakerHigh size={20} weight="fill" />
                  )}
                </Button>
              )}

              <Button
                variant="ghost"
                size="icon"
                onClick={toggleFullscreen}
                className="text-white hover:bg-white/20"
                aria-label={isFullscreen ? 'Exit fullscreen' : 'Enter fullscreen'}
              >
                {isFullscreen ? (
                  <ArrowsIn size={20} weight="fill" />
                ) : (
                  <ArrowsOut size={20} weight="fill" />
                )}
              </Button>

              {isOwn && (
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button
                      variant="ghost"
                      size="icon"
                      className="text-white hover:bg-white/20"
                      aria-label="Story options"
                    >
                      <DotsThree size={24} weight="bold" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="bg-background/95 backdrop-blur-lg">
                    <DropdownMenuItem onClick={handleSaveStory}>
                      <BookmarkSimple size={18} className="mr-2" />
                      Save to Highlight
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={handleShare}>
                      <PaperPlaneRight size={18} className="mr-2" />
                      Share Story
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              )}

              <Button
                variant="ghost"
                size="icon"
                onClick={onClose}
                className="text-white hover:bg-white/20"
                aria-label="Close story viewer"
              >
                <X size={24} weight="bold" />
              </Button>
            </div>
          </div>
        </div>

        {/* Swipe areas */}
        <div className="absolute inset-0 flex pointer-events-none">
          <div className="flex-1 cursor-w-resize" aria-label="Previous story" />
          <div className="flex-1 cursor-e-resize" aria-label="Next story" />
        </div>

        {/* Media container with pinch-zoom support */}
        <MotionView
          ref={mediaContainerRef}
          className="relative w-full h-full max-w-2xl mx-auto touch-none"
          style={{
            ...mediaContainerStyle,
            ...swipeOpacityStyle,
            ...swipeScaleStyle,
          }}
        >
          {currentStory.type === 'photo' && (
            <MotionView key={currentStory.id} style={imageEntryStyle}>
              <ProgressiveImage
                src={currentStory.mediaUrl}
                alt={currentStory.caption ?? 'Story'}
                className="w-full h-full object-contain select-none"
                aria-label={currentStory.caption ?? 'Story image'}
              />
            </MotionView>
          )}

          {currentStory.type === 'video' && (
            <video
              ref={videoRef}
              src={currentStory.mediaUrl}
              className="w-full h-full object-contain"
              autoPlay
              loop
              muted={isMuted}
              playsInline
              aria-label={`Video story by ${String(currentStory.userName ?? '')}`}
            />
          )}

          {currentStory.caption && (
            <div className="absolute bottom-24 left-0 right-0 px-4">
              <MotionView
                className="glass-strong p-4 rounded-2xl backdrop-blur-xl"
                style={captionStyle}
              >
                <p className="text-white text-center">{currentStory.caption}</p>
              </MotionView>
            </div>
          )}
        </MotionView>

        {/* Interaction area */}
        {!isOwn && (
          <div className="absolute bottom-0 left-0 right-0 z-20 p-4 space-y-3">
            <AnimatePresence>
              {showReactions && (
                <MotionView
                  key="reactions"
                  style={reactionsStyle}
                  className="glass-strong p-4 rounded-2xl backdrop-blur-xl"
                  role="dialog"
                  aria-label="React to story"
                >
                  <div className="flex justify-center gap-4">
                    {STORY_REACTION_EMOJIS.map((emoji) => (
                      <MotionView
                        key={emoji}
                        role="button"
                        tabIndex={0}
                        className="text-4xl focus:outline-none focus:ring-2 focus:ring-white rounded-lg p-2"
                        style={{
                          ...reactionButtonHover.animatedStyle,
                          ...reactionButtonTap.animatedStyle,
                        }}
                        onMouseEnter={reactionButtonHover.handleEnter}
                        onMouseLeave={reactionButtonHover.handleLeave}
                        onClick={() => {
                          reactionButtonTap.handlePress();
                          handleReaction(emoji);
                        }}
                        aria-label={`React with ${String(emoji ?? '')}`}
                        onKeyDown={(e) => {
                          if (e.key === 'Enter' || e.key === ' ') {
                            e.preventDefault();
                            reactionButtonTap.handlePress();
                            handleReaction(emoji);
                          }
                        }}
                      >
                        {emoji}
                      </MotionView>
                    ))}
                  </div>
                </MotionView>
              )}
            </AnimatePresence>

            <div className="flex items-center gap-2">
              <div className="flex-1 relative">
                <Input
                  value={replyText}
                  onChange={(e) => { setReplyText(e.target.value); }}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      handleReply();
                    }
                  }}
                  placeholder="Send a reply..."
                  className="glass-strong border-white/30 text-white placeholder:text-white/60 pr-12 backdrop-blur-xl"
                  aria-label="Reply to story"
                />
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={() => { setShowReactions(!showReactions); }}
                  className="absolute right-1 top-1/2 -translate-y-1/2 text-white hover:bg-white/20"
                  aria-label={showReactions ? 'Hide reactions' : 'Show reactions'}
                  aria-pressed={showReactions}
                >
                  <Heart size={20} weight={showReactions ? 'fill' : 'regular'} />
                </Button>
              </div>

              <Button
                onClick={handleReply}
                disabled={!replyText.trim()}
                size="icon"
                className="shrink-0 bg-white text-black hover:bg-white/90"
                aria-label="Send reply"
              >
                <PaperPlaneRight size={20} weight="fill" />
              </Button>
            </div>
          </div>
        )}

        {/* Analytics for story owner */}
        {isOwn && (
          <div className="absolute bottom-4 left-4 right-4 z-20">
            <MotionView
              className="glass-strong p-4 rounded-2xl backdrop-blur-xl"
              style={analyticsStyle}
              role="region"
              aria-label="Story analytics"
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                  <div className="text-center">
                    <p className="text-white text-2xl font-bold">{currentStory.viewCount}</p>
                    <p className="text-white/60 text-xs">Views</p>
                  </div>
                  <div className="text-center">
                    <p className="text-white text-2xl font-bold">{currentStory.reactions.length}</p>
                    <p className="text-white/60 text-xs">Reactions</p>
                  </div>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-white"
                  aria-label="View detailed insights"
                >
                  View Insights
                </Button>
              </div>
            </MotionView>
          </div>
        )}
      </div>
      {showSaveDialog && currentStory && (
        <SaveToHighlightDialog
          open={showSaveDialog}
          onOpenChange={setShowSaveDialog}
          story={currentStory}
          onSaved={() => {
            setShowSaveDialog(false);
          }}
        />
      )}
    </MotionView>
  );
}

/**
 * Advanced Checkbox Component
 * Production-ready checkbox with accessibility, animations, and design system integration
 */

import * as React from 'react'

const { forwardRef, useCallback, useMemo } = React
import { motion } from '@petspark/motion'
import { cn } from '@/lib/utils'
import { createLogger } from '@/lib/logger'

const logger = createLogger('Checkbox')

export interface CheckboxProps extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'size' | 'type'> {
  // Visual variants
  readonly variant?: 'default' | 'destructive' | 'success'
  readonly size?: 'sm' | 'md' | 'lg'

  // States
  readonly checked?: boolean
  readonly indeterminate?: boolean
  readonly error?: boolean
  readonly disabled?: boolean

  // Content
  readonly label?: string
  readonly description?: string
  readonly errorMessage?: string

  // Advanced features
  readonly trackingId?: string
  readonly disableAnimation?: boolean
  readonly customIcon?: React.ReactNode
  readonly customIndeterminateIcon?: React.ReactNode

  // Callbacks
  readonly onCheckedChange?: (checked: boolean) => void
}

export interface CheckboxRef extends HTMLInputElement {
  readonly focus: () => void
  readonly blur: () => void
}

// Checkbox variant styles
const checkboxVariants = {
  default: {
    base: 'border-primary data-[state=checked]:bg-primary data-[state=checked]:text-primary-foreground',
    focus: 'focus-visible:ring-primary'
  },
  destructive: {
    base: 'border-destructive data-[state=checked]:bg-destructive data-[state=checked]:text-destructive-foreground',
    focus: 'focus-visible:ring-destructive'
  },
  success: {
    base: 'border-green-500 data-[state=checked]:bg-green-500 data-[state=checked]:text-white',
    focus: 'focus-visible:ring-green-500'
  }
} as const

const checkboxSizes = {
  sm: {
    checkbox: 'h-3 w-3',
    icon: 'h-2.5 w-2.5',
    text: 'text-xs'
  },
  md: {
    checkbox: 'h-4 w-4',
    icon: 'h-3 w-3',
    text: 'text-sm'
  },
  lg: {
    checkbox: 'h-5 w-5',
    icon: 'h-4 w-4',
    text: 'text-base'
  }
} as const

// Animation variants
const checkboxAnimations = {
  checked: {
    scale: [0.8, 1.1, 1],
    transition: { duration: 0.2, ease: 'easeOut' }
  },
  unchecked: {
    scale: 1,
    transition: { duration: 0.1 }
  }
}

// Default check icon
const CheckIcon: React.FC<{ className?: string }> = ({ className }) => (
  <svg
    className={className}
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    stroke="currentColor"
    strokeWidth={3}
  >
    <path strokeLinecap="round" strokeLinejoin="round" d="M5 13l4 4L19 7" />
  </svg>
)

// Default indeterminate icon
const IndeterminateIcon: React.FC<{ className?: string }> = ({ className }) => (
  <svg
    className={className}
    xmlns="http://www.w3.org/2000/svg"
    fill="none"
    viewBox="0 0 24 24"
    stroke="currentColor"
    strokeWidth={3}
  >
    <path strokeLinecap="round" strokeLinejoin="round" d="M20 12H4" />
  </svg>
)

export const Checkbox = forwardRef<CheckboxRef, CheckboxProps>(
  ({
    variant = 'default',
    size = 'md',
    checked = false,
    indeterminate = false,
    error = false,
    disabled = false,
    label,
    description,
    errorMessage,
    trackingId,
    disableAnimation = false,
    customIcon,
    customIndeterminateIcon,
    onCheckedChange,
    className,
    id,
    onChange,
    ...props
  }, ref) => {
    // Generate unique ID if not provided
    const checkboxId = id ?? `checkbox-${Math.random().toString(36).substr(2, 9)}`
    const descriptionId = `${checkboxId}-description`
    const errorId = `${checkboxId}-error`

    // Determine current state
    const isChecked = indeterminate ? false : checked
    const dataState = indeterminate ? 'indeterminate' : isChecked ? 'checked' : 'unchecked'

    // Handle change
    const handleChange = useCallback((event: React.ChangeEvent<HTMLInputElement>) => {
      const newChecked = event.target.checked

      // Call original onChange
      onChange?.(event)

      // Call onCheckedChange
      onCheckedChange?.(newChecked)

      // Analytics tracking
      if (trackingId) {
        logger.info('Checkbox toggled', {
          trackingId,
          checked: newChecked,
          indeterminate,
          variant,
          size
        })
      }
    }, [onChange, onCheckedChange, trackingId, indeterminate, variant, size])

    // Checkbox classes
    const checkboxClasses = useMemo(() => cn(
      // Base styles
      'peer shrink-0 rounded-sm border border-input shadow transition-colors',
      'focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-offset-1',
      'disabled:cursor-not-allowed disabled:opacity-50',

      // Size styles
      checkboxSizes[size].checkbox,

      // Variant styles
      error ? checkboxVariants.destructive.base : checkboxVariants[variant].base,
      error ? checkboxVariants.destructive.focus : checkboxVariants[variant].focus,

      // State styles
      'data-[state=checked]:border-transparent',
      'data-[state=indeterminate]:border-transparent',

      className
    ), [variant, size, error, className])

    // Label classes
    const labelClasses = cn(
      'font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer',
      checkboxSizes[size].text,
      error && 'text-destructive'
    )

    // Icon to display
    const displayIcon = indeterminate
      ? (customIndeterminateIcon ?? <IndeterminateIcon className={checkboxSizes[size].icon} />)
      : (customIcon ?? <CheckIcon className={checkboxSizes[size].icon} />)

    // Accessibility props
    const accessibilityProps = {
      'aria-describedby': cn(
        description && descriptionId,
        error && errorMessage && errorId
      ).trim() ?? undefined,
      'aria-invalid': error,
      'aria-checked': indeterminate ? ('mixed' as const) : isChecked
    }

    return (
      <div className="space-y-2">
        <div className="flex items-start space-x-2">
          {/* Checkbox container */}
          <div className="relative flex items-center">
            {/* Hidden input */}
            <input
              ref={ref}
              id={checkboxId}
              type="checkbox"
              className="sr-only"
              checked={isChecked}
              onChange={handleChange}
              disabled={disabled}
              data-tracking-id={trackingId}
              {...accessibilityProps}
              {...props}
            />

            {/* Visual checkbox */}
            <label
              htmlFor={checkboxId}
              className={checkboxClasses}
              data-state={dataState}
            >
              {/* Checkbox icon with animation */}
              {(isChecked || indeterminate) && (
                <motion.div
                  className="flex items-center justify-center text-current"
                  initial={disableAnimation ? undefined : { scale: 0 }}
                  animate={disableAnimation ? undefined : checkboxAnimations.checked}
                  exit={disableAnimation ? undefined : checkboxAnimations.unchecked}
                >
                  {displayIcon}
                </motion.div>
              )}
            </label>
          </div>

          {/* Label and description */}
          {(label || description) && (
            <div className="flex-1 space-y-1">
              {label && (
                <label
                  htmlFor={checkboxId}
                  className={labelClasses}
                >
                  {label}
                </label>
              )}

              {description && (
                <p
                  id={descriptionId}
                  className={cn(
                    "leading-relaxed",
                    checkboxSizes[size].text === 'text-xs' ? 'text-xs' : 'text-xs',
                    error ? 'text-destructive/70' : 'text-muted-foreground'
                  )}
                >
                  {description}
                </p>
              )}
            </div>
          )}
        </div>

        {/* Error message */}
        {error && errorMessage && (
          <p id={errorId} className="text-sm text-destructive">
            {errorMessage}
          </p>
        )}
      </div>
    )
  }
)

Checkbox.displayName = 'Checkbox'

// Checkbox group component for managing multiple checkboxes
export interface CheckboxGroupProps {
  readonly value?: string[]
  readonly defaultValue?: string[]
  readonly onValueChange?: (value: string[]) => void
  readonly disabled?: boolean
  readonly orientation?: 'horizontal' | 'vertical'
  readonly children: React.ReactNode
  readonly className?: string
}

export const CheckboxGroup: React.FC<CheckboxGroupProps> = ({
  value,
  defaultValue = [],
  onValueChange,
  disabled = false,
  orientation = 'vertical',
  children,
  className
}) => {
  const [internalValue, setInternalValue] = React.useState(defaultValue)
  const currentValue = value ?? internalValue

  const handleValueChange = useCallback((itemValue: string, checked: boolean) => {
    const newValue = checked
      ? [...currentValue, itemValue]
      : currentValue.filter(v => v !== itemValue)

    if (value === undefined) {
      setInternalValue(newValue)
    }

    onValueChange?.(newValue)
  }, [currentValue, value, onValueChange])

  return (
    <div
      className={cn(
        'space-y-2',
        orientation === 'horizontal' && 'flex flex-wrap gap-4 space-y-0',
        className
      )}
      role="group"
    >
      {React.Children.map(children, (child, index) => {
        if (React.isValidElement(child) && child.type === Checkbox) {
          const itemValue = child.props.value ?? String(index)
          const isChecked = currentValue.includes(itemValue)

          return React.cloneElement(child as React.ReactElement<CheckboxProps>, {
            checked: isChecked,
            disabled: disabled ?? child.props.disabled,
            onCheckedChange: (checked: boolean) => {
              child.props.onCheckedChange?.(checked)
              handleValueChange(itemValue, checked)
            }
          })
        }

        return child
      })}
    </div>
  )
}

CheckboxGroup.displayName = 'CheckboxGroup'

/**
 * Dialog Component Storybook Stories
 * Location: apps/web/src/components/ui/dialog.stories.tsx
 */

import type { Meta, StoryObj } from '@storybook/react';
import { useState } from 'react';
import { Button } from './button';
import {
  Dialog,
