});

/**
 * Playdates View
 *
 * Main playdates view with scheduler, map, and list
 */

'use client';

import { useState, useEffect, useCallback } from 'react';
import { Plus } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { PageTransitionWrapper } from '@/components/ui/page-transition-wrapper';
import { PlaydateScheduler } from '@/components/playdates/PlaydateScheduler';
import { PlaydateMap } from '@/components/playdates/PlaydateMap';
import { PlaydateCard } from '@/components/playdates/PlaydateCard';
import { PremiumCard } from '@/components/enhanced/PremiumCard';
import { playdatesClient } from '@petspark/core';
import type { Playdate, PlaydateLocation } from '@petspark/core';
import { cn } from '@/lib/utils';
import { getTypographyClasses } from '@/lib/typography';
import { createLogger } from '@/lib/logger';
import { toast } from 'sonner';

const logger = createLogger('PlaydatesView');

export function PlaydatesView(): React.JSX.Element {
  const [playdates, setPlaydates] = useState<Playdate[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showScheduler, setShowScheduler] = useState(false);
  const [currentLocation, setCurrentLocation] = useState<{ latitude: number; longitude: number } | undefined>();

  useEffect(() => {
    void loadPlaydates();
    void getCurrentLocation();
  }, []);

  const getCurrentLocation = useCallback(() => {
    if (!navigator.geolocation) {
      return;
    }

    navigator.geolocation.getCurrentPosition(
      (position) => {
        setCurrentLocation({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
        });
      },
      (error) => {
        logger.error('Failed to get current location', error);
      }
    );
  }, []);

  const loadPlaydates = async (): Promise<void> => {
    try {
      setIsLoading(true);
      const data = await playdatesClient.getPlaydates();
      setPlaydates(data);
    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));
      logger.error('Failed to load playdates', err);
      void toast.error('Failed to load playdates');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSchedule = useCallback(
    async (playdateData: {
      title: string;
      description?: string;
      scheduledAt: string;
      duration: number;
      location: PlaydateLocation;
      isPublic: boolean;
      maxParticipants?: number;
      trustedContactId?: string;
    }) => {
      try {
        const newPlaydate = await playdatesClient.createPlaydate({
          ...playdateData,
          petId: 'current-pet-id',
          visibility: playdateData.isPublic ? 'public' : 'private',
        });
        setPlaydates((prev) => [newPlaydate, ...prev]);
        setShowScheduler(false);
        void toast.success('Playdate scheduled!');
      } catch (error) {
        const err = error instanceof Error ? error : new Error(String(error));
        logger.error('Failed to schedule playdate', err);
        void toast.error('Failed to schedule playdate');
      }
    },
    []
  );

  const handleJoin = useCallback(
    async (playdateId: string) => {
      try {
        const updated = await playdatesClient.joinPlaydate(playdateId);
        setPlaydates((prev) => prev.map((p) => (p.id === playdateId ? updated : p)));
        void toast.success('Joined playdate!');
      } catch (error) {
        const err = error instanceof Error ? error : new Error(String(error));
        logger.error('Failed to join playdate', err);
        void toast.error('Failed to join playdate');
      }
    },
    []
  );

  const handleCheckIn = useCallback(
    async (playdateId: string) => {
      try {
        await playdatesClient.checkIn(playdateId);
        void toast.success('Checked in!');
      } catch (error) {
        const err = error instanceof Error ? error : new Error(String(error));
        logger.error('Failed to check in', err);
        void toast.error('Failed to check in');
      }
    },
    []
  );

  const upcomingPlaydates = playdates.filter(
    (p) => new Date(p.scheduledAt) > new Date() && p.status === 'confirmed'
  );
  const nearbyPlaydates = currentLocation
    ? upcomingPlaydates
      .map((p) => ({
        playdate: p,
        distance: Math.sqrt(
          Math.pow(p.location.latitude - currentLocation.latitude, 2) +
          Math.pow(p.location.longitude - currentLocation.longitude, 2)
        ),
      }))
      .sort((a, b) => a.distance - b.distance)
      .slice(0, 10)
      .map((item) => item.playdate)
    : upcomingPlaydates;

  return (
    <PageTransitionWrapper>
      <div className="container mx-auto p-6 space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className={cn(getTypographyClasses('h1'), 'mb-2')}>Playdates</h1>
            <p className={cn(getTypographyClasses('bodyMuted'), 'text-sm')}>
              Schedule and join playdates with other pet owners
            </p>
          </div>
          <Button onClick={() => setShowScheduler(true)}>
            <Plus className="size-4 mr-2" />
            Schedule Playdate
          </Button>
        </div>

        <Tabs defaultValue="list" className="space-y-4">
          <TabsList>
            <TabsTrigger value="list">List</TabsTrigger>
            <TabsTrigger value="map">Map</TabsTrigger>
          </TabsList>

          <TabsContent value="list" className="space-y-4">
            {isLoading ? (
              <PremiumCard variant="glass" className="p-8 text-center">
                <p className={cn(getTypographyClasses('bodyMuted'), 'text-sm')}>Loading playdates...</p>
              </PremiumCard>
            ) : upcomingPlaydates.length === 0 ? (
              <PremiumCard variant="glass" className="p-8 text-center">
                <p className={cn(getTypographyClasses('bodyMuted'), 'text-sm')}>
                  No upcoming playdates. Schedule one to get started!
                </p>
              </PremiumCard>
            ) : (
              <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
                {upcomingPlaydates.map((playdate) => (
                  <PlaydateCard
                    key={playdate.id}
                    playdate={playdate}
                    onJoin={() => handleJoin(playdate.id)}
                    onCheckIn={() => void handleCheckIn(playdate.id)}
                    isParticipant={playdate.participants.some((p) => p.id === 'current-user-id')}
                  />
                ))}
              </div>
            )}
          </TabsContent>

          <TabsContent value="map">
            <PlaydateMap
              playdates={nearbyPlaydates}
              currentLocation={currentLocation}
              onPlaydateClick={(_playdate) => {
                // Handle playdate click
              }}
            />
          </TabsContent>
        </Tabs>

        <Dialog open={showScheduler} onOpenChange={setShowScheduler}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className={cn(getTypographyClasses('h2'))}>Schedule Playdate</DialogTitle>
            </DialogHeader>
            <PlaydateScheduler
              onSchedule={handleSchedule}
              onCancel={() => setShowScheduler(false)}
            />
          </DialogContent>
        </Dialog>
      </div>
    </PageTransitionWrapper>
  );
}

import { useState, useEffect, useMemo } from 'react';
import { useStorage } from '@/hooks/use-storage';
import { calculateDistance } from '@/lib/maps/utils';
import type { Location, Place } from '@/lib/maps/types';
import { useMapConfig } from '@/lib/maps/useMapConfig';
import { toast } from 'sonner';
import { useApp } from '@/contexts/AppContext';
import { haptics } from '@/lib/haptics';
import { isTruthy } from '@petspark/shared';

export interface UseMapPlacesReturn {
  nearbyPlaces: Place[];
  savedPlaces: string[];
  filteredPlaces: Place[];
  searchQuery: string;
  selectedCategory: string | null;
  setSearchQuery: (query: string) => void;
  setSelectedCategory: (category: string | null) => void;
  handleCategoryFilter: (categoryId: string) => void;
  handleSavePlace: (placeId: string) => void;
  generateDemoPlaces: (center: Location) => void;
}

export function useMapPlaces(userLocation: Location | null, radiusKm: number): UseMapPlacesReturn {
  const { t } = useApp();
  const { PLACE_CATEGORIES } = useMapConfig();
  const [nearbyPlaces, setNearbyPlaces] = useState<Place[]>([]);
  const [savedPlaces, setSavedPlaces] = useStorage<string[]>('saved-places', []);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);

  useEffect(() => {
    if (isTruthy(userLocation)) {
      generateDemoPlaces(userLocation);
    }
  }, [userLocation, radiusKm]);

  const generateDemoPlaces = (center: Location): void => {
    const places: Place[] = [];
    const categories = PLACE_CATEGORIES;

    for (let i = 0; i < 20; i++) {
      const category = categories[Math.floor(Math.random() * categories.length)];
      if (!category) continue;
      const angle = (Math.PI * 2 * i) / 20;
      const dist = Math.random() * radiusKm;
      const deltaLat = (dist / 111) * Math.cos(angle);
      const deltaLng = (dist / (111 * Math.cos((center.lat * Math.PI) / 180))) * Math.sin(angle);

      const location: Location = {
        lat: center.lat + deltaLat,
        lng: center.lng + deltaLng,
      };

      places.push({
        id: `place-${String(i ?? '')}`,
        name: `${String(category.name)} ${String(i + 1)}`,
        description: `Great ${String(category.name.toLowerCase() ?? '')} in your area`,
        category: category.id,
        location,
        address: `${String(Math.floor(Math.random() * 999) ?? '')} Main St, City`,
        phone: `+1-555-${String(Math.floor(Math.random() * 9000) + 1000)}`,
        hours: '9:00 AM - 6:00 PM',
        photos: [
          `https://images.unsplash.com/photo-${String(1560807700000 + i * 1000000)}?w=400&q=80`,
        ],
        verified: Math.random() > 0.3,
        petFriendly: true,
        rating: 3.5 + Math.random() * 1.5,
        reviewCount: Math.floor(Math.random() * 200),
        amenities: ['Water Bowl', 'Outdoor Space', 'Pet-Friendly Staff'],
        distance: calculateDistance(center, location),
        isOpen: Math.random() > 0.2,
        moderationStatus: 'approved',
      });
    }

    setNearbyPlaces(places.sort((a, b) => (a.distance ?? 0) - (b.distance ?? 0)));
  };

  const handleCategoryFilter = (categoryId: string): void => {
    haptics.trigger('selection');
    setSelectedCategory(selectedCategory === categoryId ? null : categoryId);
  };

  const handleSavePlace = (placeId: string): void => {
    haptics.trigger('medium');
    setSavedPlaces((current) => {
      const currentPlaces = current ?? [];
      if (currentPlaces.includes(placeId)) {
        void toast.info(t.map?.placeRemoved ?? 'Place removed from saved');
        return currentPlaces.filter((id) => id !== placeId);
      } else {
        void toast.success(t.map?.placeSaved ?? 'Place saved');
        return [...currentPlaces, placeId];
      }
    });
  };

  const filteredPlaces = useMemo(() => {
    let filtered = nearbyPlaces;

    if (selectedCategory) {
      filtered = filtered.filter((place) => place.category === selectedCategory);
    }

    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (place) =>
          place.name.toLowerCase().includes(query) ||
          place.description?.toLowerCase().includes(query) ||
          place.category.toLowerCase().includes(query)
      );
    }

    return filtered;
  }, [nearbyPlaces, selectedCategory, searchQuery]);

  return {
    nearbyPlaces,
    savedPlaces: savedPlaces ?? [],
    filteredPlaces,
    searchQuery,
    selectedCategory,
    setSearchQuery,
    setSelectedCategory,
    handleCategoryFilter,
    handleSavePlace,
    generateDemoPlaces,
  };
}

/**
 * Feature Flags Configuration
 *
 * Controls premium features and effects rollout
 */

export interface FeatureFlags {
  enableHoloBackground: boolean;
  enableGlowTrail: boolean;
  enableSendPing: boolean;
  enableMessagePeek: boolean;
  enableSmartImage: boolean;
}

const defaultFlags: FeatureFlags = {
  enableHoloBackground: true,
  enableGlowTrail: true,
  enableSendPing: true,
  enableMessagePeek: true,
  enableSmartImage: true,
};

/**
 * Get feature flags from environment or defaults
 */
export function getFeatureFlags(): FeatureFlags {
  // In production, could fetch from remote config
  if (typeof window !== 'undefined' && window) {
    const windowWithFlags = window as Window & { __FEATURE_FLAGS__?: Partial<FeatureFlags> };
    const runtimeFlags = windowWithFlags?.__FEATURE_FLAGS__;
    if (runtimeFlags) {
      return { ...defaultFlags, ...runtimeFlags };
    }
  }

  // Check environment variables
  if (typeof import.meta !== 'undefined' && import.meta.env) {
    return {
      enableHoloBackground: import.meta.env.VITE_ENABLE_HOLO_BG !== 'false',
      enableGlowTrail: import.meta.env.VITE_ENABLE_GLOW_TRAIL !== 'false',
      enableSendPing: import.meta.env.VITE_ENABLE_SEND_PING !== 'false',
      enableMessagePeek: import.meta.env.VITE_ENABLE_MESSAGE_PEEK !== 'false',
      enableSmartImage: import.meta.env.VITE_ENABLE_SMART_IMAGE !== 'false',
    };
  }

  return defaultFlags;
}

/**
 * Hook to use feature flags
 */
export function useFeatureFlags(): FeatureFlags {
  return getFeatureFlags();
}

// apps/web/src/contexts/CallContext.tsx
'use client';

import {
  createContext,
  useCallback,
  useContext,
  useEffect,
  useMemo,
  useState,
  type ReactNode,
} from 'react';
import type { CallOfferSignal, CallSession, CallStatus, CallSignal } from '@petspark/core';
import type { CallSession as LocalCallSession, CallStatus as LocalCallStatus, Call, CallParticipant } from '@/lib/call-types';
import { useWebRtcCall } from '@/hooks/calls/useWebRtcCall';
import { useAuth } from '@/contexts/AuthContext';
import { IncomingCallBanner } from '@/components/calls/IncomingCallBanner';
import { CallOverlay } from '@/components/calls/CallOverlay';
import { createLogger } from '@/lib/logger';

const logger = createLogger('CallContext');

// Adapter functions to convert core types to local types
function adaptCallSession(coreSession: CallSession | null): LocalCallSession | null {
  if (!coreSession) return null;

  // Create a basic Call object from the core session
  const call: Call = {
    id: coreSession.id,
    roomId: coreSession.id, // Use session id as room id
    type: 'video', // Default to video, could be enhanced later
    initiatorId: coreSession.direction === 'outgoing' ? coreSession.localParticipant.id : (coreSession.remoteParticipant?.id ?? ''),
    recipientId: coreSession.direction === 'incoming' ? coreSession.localParticipant.id : (coreSession.remoteParticipant?.id ?? ''),
    status: adaptCallStatus(coreSession.status),
    startTime: coreSession.startedAt,
    endTime: coreSession.endedAt,
    duration: coreSession.endedAt ? new Date(coreSession.endedAt).getTime() - new Date(coreSession.startedAt).getTime() : 0,
    quality: 'good', // Default quality
    videoQuality: '720p', // Default video quality
  };

  // Adapt participants
  const localParticipant: CallParticipant = {
    id: coreSession.localParticipant.id,
    name: coreSession.localParticipant.displayName,
    avatar: coreSession.localParticipant.avatarUrl ?? undefined,
    isMuted: coreSession.localParticipant.microphone === 'muted',
    isVideoEnabled: coreSession.localParticipant.camera === 'enabled',
  };

  const remoteParticipant: CallParticipant = coreSession.remoteParticipant ? {
    id: coreSession.remoteParticipant.id,
    name: coreSession.remoteParticipant.displayName,
    avatar: coreSession.remoteParticipant.avatarUrl ?? undefined,
    isMuted: coreSession.remoteParticipant.microphone === 'muted',
    isVideoEnabled: coreSession.remoteParticipant.camera === 'enabled',
  } : {
    id: 'unknown',
    name: 'Unknown',
    isMuted: false,
    isVideoEnabled: false,
  };

  return {
    call,
    localParticipant,
    remoteParticipant,
    isMinimized: false, // Default to not minimized
    videoQuality: '720p', // Default video quality
  };
}

function adaptCallStatus(coreStatus: CallStatus): LocalCallStatus {
  switch (coreStatus) {
    case 'idle': return 'idle';
    case 'ringing': return 'ringing';
    case 'connecting': return 'connecting';
    case 'in-call': return 'active';
    case 'ended': return 'ended';
    case 'failed': return 'failed';
    default: return 'idle';
  }
}

export interface CallProviderProps {
  readonly signalingUrl: string;
  readonly signalingToken?: string;
  readonly children: ReactNode;
}

export interface CallContextValue {
  readonly session: CallSession | null;
  readonly status: CallStatus;
  readonly isMuted: boolean;
  readonly isCameraOff: boolean;
  readonly isScreenSharing: boolean;

  startCall: (params: {
    readonly remoteUserId: string;
    readonly remoteDisplayName: string;
    readonly remoteAvatarUrl?: string | null;
  }) => Promise<void>;

  acceptIncomingCall: () => Promise<void>;
  rejectIncomingCall: (reason?: string) => void;
  endCall: (reason?: string) => void;

  toggleMute: () => void;
  toggleCamera: () => void;
  toggleScreenShare: () => Promise<void>;
}

const CallContext = createContext<CallContextValue | undefined>(undefined);

function useIncomingOfferListener(
  userId: string | undefined,
  signalingClient: { onSignal: (h: (s: CallSignal) => Promise<void>) => () => void },
  handleIncomingOffer: (signal: CallOfferSignal) => void,
  setIncomingOffer: (s: CallOfferSignal | null) => void
): void {
  useEffect(() => {
    if (!userId) return;
    const unsubscribe = signalingClient.onSignal(async (signal) => {
      if (signal.type !== 'call-offer' || signal.toUserId !== userId) return;
      logger.debug('Incoming call offer received', {
        fromUserId: signal.fromUserId,
        callId: signal.callId,
      });
      setIncomingOffer(signal);
      handleIncomingOffer(signal);
    });
    return () => unsubscribe();
  }, [handleIncomingOffer, signalingClient, setIncomingOffer, userId]);
}

function IncomingBannerWrapper({
  incomingOffer,
  session,
  status,
  onAccept,
  onReject,
}: {
  incomingOffer: CallOfferSignal | null;
  session: CallSession | null;
  status: CallStatus;
  onAccept: () => void;
  onReject: (reason?: string) => void;
}): React.JSX.Element {
  const callerName =
    session?.direction === 'incoming' ? (session.remoteParticipant?.displayName ?? 'Incoming call') : 'Incoming call';
  return (
    <IncomingCallBanner
      open={
        Boolean(incomingOffer) &&
        Boolean(session) &&
        session?.direction === 'incoming' &&
        status === 'ringing'
      }
      callerName={callerName}
      onAccept={onAccept}
      onReject={() => onReject('User rejected call')}
    />
  );
}

function OverlayWrapper({
  overlayOpen,
  session,
  status,
  localStream,
  remoteStream,
  isMuted,
  isCameraOff,
  isScreenSharing,
  onClose,
  onEnd,
  onToggleMute,
  onToggleCamera,
  onToggleScreenShare,
}: {
  overlayOpen: boolean;
  session: CallSession | null;
  status: CallStatus;
  localStream: MediaStream | null;
  remoteStream: MediaStream | null;
  isMuted: boolean;
  isCameraOff: boolean;
  isScreenSharing: boolean;
  onClose: () => void;
  onEnd: (reason?: string) => void;
  onToggleMute: () => void;
  onToggleCamera: () => void;
  onToggleScreenShare: () => void;
}): React.JSX.Element {
  return (
    <CallOverlay
      open={overlayOpen && !!session && status !== 'idle'}
      session={adaptCallSession(session)}
      localStream={localStream}
      remoteStream={remoteStream}
      status={adaptCallStatus(status)}
      isMuted={isMuted}
      isCameraOff={isCameraOff}
      isScreenSharing={isScreenSharing}
      onClose={onClose}
      onEndCall={() => onEnd('local-end')}
      onToggleMute={onToggleMute}
      onToggleCamera={onToggleCamera}
      onToggleScreenShare={onToggleScreenShare}
    />
  );
}

export function CallProvider({
  signalingUrl,
  signalingToken,
  children,
}: CallProviderProps): React.JSX.Element {
  const { user } = useAuth();
  const {
    session,
    status,
    localStream,
    remoteStream,
    isMuted,
    isCameraOff,
    isScreenSharing,
    signalingClient,
    startOutgoingCall,
    handleIncomingOffer,
    acceptIncomingCall,
    rejectIncomingCall,
    endCall,
    toggleMute,
    toggleCamera,
    toggleScreenShare,
  } = useWebRtcCall({
    signaling: { baseUrl: signalingUrl, token: signalingToken },
    localUserId: user?.id ?? '',
    localDisplayName: user?.displayName ?? user?.email ?? 'Unknown User',
    localAvatarUrl: user?.avatarUrl ?? null,
  });
  const [incomingOffer, setIncomingOffer] = useState<CallOfferSignal | null>(null);
  const [overlayOpen, setOverlayOpen] = useState(false);
  useIncomingOfferListener(
    user?.id,
    { onSignal: (h) => signalingClient.onSignal(h) },
    handleIncomingOffer,
    setIncomingOffer
  );
  useEffect(() => {
    if (status === 'ended' || status === 'failed' || status === 'idle') {
      setOverlayOpen(false);
      setIncomingOffer(null);
    }
  }, [status]);
  const startCall = useCallback(
    async ({
      remoteUserId,
      remoteDisplayName,
      remoteAvatarUrl,
    }: {
      remoteUserId: string;
      remoteDisplayName: string;
      remoteAvatarUrl?: string | null;
    }) => {
      const callId = crypto.randomUUID();
      await startOutgoingCall({ callId, remoteUserId, remoteDisplayName, remoteAvatarUrl });
      setOverlayOpen(true);
    },
    [startOutgoingCall]
  );
  const handleAccept = useCallback(async () => {
    await acceptIncomingCall();
    setIncomingOffer(null);
    setOverlayOpen(true);
  }, [acceptIncomingCall]);
  const handleReject = useCallback(
    (reason?: string) => {
      rejectIncomingCall(reason);
      setIncomingOffer(null);
      setOverlayOpen(false);
    },
    [rejectIncomingCall]
  );
  const handleEnd = useCallback(
    (reason?: string) => {
      endCall(reason);
      setOverlayOpen(false);
      setIncomingOffer(null);
    },
    [endCall]
  );
  const value: CallContextValue = useMemo(
    () => ({
      session,
      status,
      isMuted,
      isCameraOff,
      isScreenSharing,
      startCall,
      acceptIncomingCall: handleAccept,
      rejectIncomingCall: handleReject,
      endCall: handleEnd,
      toggleMute,
      toggleCamera,
      toggleScreenShare,
    }),
    [
      session,
      status,
      isMuted,
      isCameraOff,
      isScreenSharing,
      startCall,
      handleAccept,
      handleReject,
      handleEnd,
      toggleMute,
      toggleCamera,
      toggleScreenShare,
    ]
  );
  return (
    <CallContext.Provider value={value}>
      {children}
      <IncomingBannerWrapper
        incomingOffer={incomingOffer}
        session={session}
        status={status}
        onAccept={() => {
          void handleAccept();
        }}
        onReject={handleReject}
      />
      <OverlayWrapper
        overlayOpen={overlayOpen}
        session={session}
        status={status}
        localStream={localStream}
        remoteStream={remoteStream}
        isMuted={isMuted}
        isCameraOff={isCameraOff}
        isScreenSharing={isScreenSharing}
        onClose={() => setOverlayOpen(false)}
        onEnd={handleEnd}
        onToggleMute={toggleMute}
        onToggleCamera={toggleCamera}
        onToggleScreenShare={() => {
          void toggleScreenShare();
        }}
      />
    </CallContext.Provider>
  );
}

export function useCall(): CallContextValue {
  const ctx = CallContext;
  const value = useContext(ctx);
  if (!value) {
    throw new Error('useCall must be used within a CallProvider');
  }
  return value;
}

/**
 * Theme Context Tests
 */

import { describe, it, expect, vi, beforeEach, afterEach } from 'vitest';
import { renderHook, act, render } from '@testing-library/react';
import { type ReactNode } from 'react';
import { ThemeProvider, useThemeSystem } from '../ThemeContext';
import { useStorage } from '@/hooks/use-storage';
import { applyTheme } from '@/lib/themes';

vi.mock('@/hooks/use-storage');
vi.mock('@/lib/themes', () => ({
    applyTheme: vi.fn(),
    ThemeMode: {},
}));

const mockUseStorage = vi.mocked(useStorage);
const mockApplyTheme = vi.mocked(applyTheme);

describe('ThemeContext', () => {
    const mockSetModePromise = vi.fn().mockResolvedValue(undefined);

    beforeEach(() => {
        vi.clearAllMocks();
        mockUseStorage.mockImplementation((key: string, defaultValue: unknown) => {
            if (key === 'theme-mode-v2') {
                return [defaultValue as 'light' | 'dark', mockSetModePromise, vi.fn()];
            }
            return [defaultValue, vi.fn(), vi.fn()];
        });
    });

    afterEach(() => {
        vi.clearAllMocks();
    });

    describe('ThemeProvider', () => {
        it('provides theme context to children', () => {
            const wrapper = ({ children }: { children: ReactNode }) => (
                <ThemeProvider>{children}</ThemeProvider>
            );

            const { result } = renderHook(() => useThemeSystem(), { wrapper });

            expect(result.current.mode).toBe('light');
            expect(result.current.setMode).toBeDefined();
            expect(result.current.toggle).toBeDefined();
            expect(result.current.tokens).toBeDefined();
            expect(result.current.rawTokens).toBeDefined();
        });

        it('calls applyTheme when mode changes', () => {
            mockUseStorage.mockImplementation((key: string, defaultValue: unknown) => {
                if (key === 'theme-mode-v2') {
                    return ['dark' as 'light' | 'dark', mockSetModePromise, vi.fn()];
                }
                return [defaultValue, vi.fn(), vi.fn()];
            });

            const wrapper = ({ children }: { children: ReactNode }) => (
                <ThemeProvider>{children}</ThemeProvider>
            );

            renderHook(() => useThemeSystem(), { wrapper });

            expect(mockApplyTheme).toHaveBeenCalledWith('dark');
        });

        it('calls applyTheme on initial render with light mode', () => {
            const wrapper = ({ children }: { children: ReactNode }) => (
                <ThemeProvider>{children}</ThemeProvider>
            );

            renderHook(() => useThemeSystem(), { wrapper });

            expect(mockApplyTheme).toHaveBeenCalledWith('light');
        });
    });

    describe('useThemeSystem', () => {
        it('throws error when used outside ThemeProvider', () => {
            const errorSpy = vi.spyOn(console, 'error').mockImplementation(() => {
                // Mock implementation to suppress React error logging
            });

            expect(() => {
                renderHook(() => useThemeSystem());
            }).toThrow('useThemeSystem must be used within ThemeProvider');

