/**
 * Sound Feedback System (Web)
 *
 * Provides volume-aware sound effects for UI interactions:
 * - Subtle UI ticks for actions
 * - Celebration sounds for matches
 * - Volume-aware playback
 * - User preference toggles
 * - Haptic feedback synchronization
 * - Reduced motion awareness
 *
 * Location: apps/web/src/effects/core/sound-feedback.ts
 */

import { createLogger } from '@/lib/logger';
import { triggerHapticByContext } from '@/effects/chat/core/haptic-manager';
import { isReduceMotionEnabled } from '@/effects/chat/core/reduced-motion';
import type { HapticContext } from '@/effects/chat/core/haptic-manager';

const logger = createLogger('sound-feedback');

/**
 * Sound type
 */
export type SoundType =
  | 'tap'
  | 'success'
  | 'error'
  | 'notification'
  | 'match'
  | 'send'
  | 'receive'
  | 'delete'
  | 'swipe'
  | 'longPress'
  | 'reaction'
  | 'reply';

/**
 * Sound context mapping to haptic contexts
 */
const SOUND_TO_HAPTIC_CONTEXT: Record<SoundType, HapticContext> = {
  tap: 'tap',
  success: 'success',
  error: 'error',
  notification: 'success',
  match: 'success',
  send: 'send',
  receive: 'receive',
  delete: 'delete',
  swipe: 'swipe',
  longPress: 'longPress',
  reaction: 'reaction',
  reply: 'reply',
};

/**
 * Sound feedback options
 */
export interface SoundFeedbackOptions {
  enabled?: boolean;
  volume?: number; // 0-1
  muted?: boolean;
  hapticEnabled?: boolean;
  respectReducedMotion?: boolean;
}

/**
 * Sound Feedback Service
 */
export class SoundFeedbackService {
  private enabled: boolean;
  private volume: number;
  private muted: boolean;
  private hapticEnabled: boolean;
  private respectReducedMotion: boolean;
  private audioContext: AudioContext | null = null;
  private soundCache = new Map<SoundType, AudioBuffer>();
  private lastPlayTime = 0;
  private cooldownMs = 50; // Minimum time between sounds

  constructor(options: SoundFeedbackOptions = {}) {
    this.enabled = options.enabled ?? true;
    this.volume = Math.max(0, Math.min(1, options.volume ?? 0.3)); // Default 30% volume
    this.muted = options.muted ?? false;
    this.hapticEnabled = options.hapticEnabled ?? true;
    this.respectReducedMotion = options.respectReducedMotion ?? true;

    // Initialize audio context
    if (typeof window !== 'undefined' && 'AudioContext' in window) {
      try {
        this.audioContext = new AudioContext();
      } catch (error) {
        const err = error instanceof Error ? error : new Error(String(error));
        logger.warn('Failed to initialize AudioContext', err);
      }
    }
  }

  /**
   * Play sound effect with optional haptic feedback
   */
  async play(soundType: SoundType, options: { haptic?: boolean } = {}): Promise<void> {
    // Check if sound is enabled and not muted
    if (!this.enabled || this.muted || !this.audioContext) {
      return;
    }

    // Respect reduced motion
    if (this.respectReducedMotion && isReduceMotionEnabled()) {
      // Only trigger haptic if enabled, skip sound
      if (this.hapticEnabled && (options.haptic ?? true)) {
        const hapticContext = SOUND_TO_HAPTIC_CONTEXT[soundType];
        if (hapticContext) {
          triggerHapticByContext(hapticContext, false);
        }
      }
      return;
    }

    // Cooldown check
    const now =
      typeof performance !== 'undefined' && performance.now ? performance.now() : Date.now();
    if (now - this.lastPlayTime < this.cooldownMs) {
      return;
    }
    this.lastPlayTime = now;

    try {
      // Trigger haptic feedback if enabled
      if (this.hapticEnabled && (options.haptic ?? true)) {
        const hapticContext = SOUND_TO_HAPTIC_CONTEXT[soundType];
        if (hapticContext) {
          triggerHapticByContext(hapticContext, false);
        }
      }

      // Resume audio context if suspended (browser autoplay policy)
      if (this.audioContext.state === 'suspended') {
        await this.audioContext.resume();
      }

      // Get or generate sound buffer
      const buffer = await this.getSoundBuffer(soundType);
      if (!buffer) {
        return;
      }

      // Create buffer source
      const source = this.audioContext.createBufferSource();
      const gainNode = this.audioContext.createGain();

      source.buffer = buffer;
      gainNode.gain.value = this.volume;

      // Connect nodes
      source.connect(gainNode);
      gainNode.connect(this.audioContext.destination);

      // Play sound
      source.start(0);

      logger.debug('Played sound', {
        soundType,
        volume: this.volume,
        hapticTriggered: this.hapticEnabled && (options.haptic ?? true),
      });
    } catch (error) {
      const err = error instanceof Error ? error : new Error(String(error));
      logger.warn('Failed to play sound', err, {
        soundType,
      });
    }
  }

  /**
   * Get or generate sound buffer
   */
  private getSoundBuffer(soundType: SoundType): Promise<AudioBuffer | null> {
    // Check cache
    const cached = this.soundCache.get(soundType);
    if (cached) {
      return Promise.resolve(cached);
    }

    if (!this.audioContext) {
      return Promise.resolve(null);
    }

    // Generate sound buffer based on type
    const buffer = this.generateSound(soundType);
    if (buffer) {
      this.soundCache.set(soundType, buffer);
    }

    return Promise.resolve(buffer);
  }

  /**
   * Generate sound waveform
   */
  private generateSound(soundType: SoundType): AudioBuffer | null {
    if (!this.audioContext) {
      return null;
    }

    const sampleRate = this.audioContext.sampleRate;
    const duration = this.getSoundDuration(soundType);
    const frameCount = Math.floor(sampleRate * duration);
    const buffer = this.audioContext.createBuffer(1, frameCount, sampleRate);
    const data = buffer.getChannelData(0);

    // Generate waveform based on sound type
    switch (soundType) {
      case 'tap':
        // Short, high-pitched click
        this.generateClick(data, sampleRate, 0.01, 800);
        break;
      case 'success':
        // Rising tone
        this.generateRisingTone(data, sampleRate, duration);
        break;
      case 'error':
        // Low, descending tone
        this.generateDescendingTone(data, sampleRate, duration);
        break;
      case 'notification':
        // Pleasant chime
        this.generateChime(data, sampleRate, duration);
        break;
      case 'match':
        // Celebration chord
        this.generateCelebrationChord(data, sampleRate, duration);
        break;
      case 'send':
        // Whoosh sound
        this.generateWhoosh(data, sampleRate, duration);
        break;
      case 'receive':
        // Soft pop
        this.generatePop(data, sampleRate, duration);
        break;
      case 'delete':
        // Sharp click
        this.generateClick(data, sampleRate, duration, 400);
        break;
      case 'swipe':
        // Subtle swipe sound
        this.generateSwipe(data, sampleRate, duration);
        break;
      case 'longPress':
        // Haptic-like buzz
        this.generateBuzz(data, sampleRate, duration);
        break;
      case 'reaction':
        // Quick pop
        this.generatePop(data, sampleRate, 0.08);
        break;
      case 'reply':
        // Subtle whoosh
        this.generateWhoosh(data, sampleRate, 0.12);
        break;
    }

    return buffer;
  }

  /**
   * Get sound duration based on type
   */
  private getSoundDuration(soundType: SoundType): number {
    switch (soundType) {
      case 'tap':
        return 0.05; // 50ms
      case 'success':
        return 0.2; // 200ms
      case 'error':
        return 0.3; // 300ms
      case 'notification':
        return 0.25; // 250ms
      case 'match':
        return 0.6; // 600ms
      case 'send':
        return 0.15; // 150ms
      case 'receive':
        return 0.1; // 100ms
      case 'delete':
        return 0.08; // 80ms
      case 'swipe':
        return 0.12; // 120ms
      case 'longPress':
        return 0.2; // 200ms
      case 'reaction':
        return 0.08; // 80ms
      case 'reply':
        return 0.12; // 120ms
      default:
        return 0.1;
    }
  }

  /**
   * Generate click sound
   */
  private generateClick(
    data: Float32Array,
    sampleRate: number,
    duration: number,
    frequency: number
  ): void {
    const frameCount = data.length;
    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const amplitude = Math.exp(-t * 30); // Exponential decay
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude;
    }
  }

  /**
   * Generate rising tone
   */
  private generateRisingTone(data: Float32Array, sampleRate: number, duration: number): void {
    const frameCount = data.length;
    const startFreq = 400;
    const endFreq = 600;

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const progress = t / duration;
      const frequency = startFreq + (endFreq - startFreq) * progress;
      const amplitude = Math.sin(Math.PI * progress); // Fade in and out
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude * 0.5;
    }
  }

  /**
   * Generate descending tone
   */
  private generateDescendingTone(data: Float32Array, sampleRate: number, duration: number): void {
    const frameCount = data.length;
    const startFreq = 300;
    const endFreq = 200;

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const progress = t / duration;
      const frequency = startFreq + (endFreq - startFreq) * progress;
      const amplitude = Math.sin(Math.PI * progress);
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude * 0.5;
    }
  }

  /**
   * Generate chime
   */
  private generateChime(data: Float32Array, sampleRate: number, duration: number): void {
    const frameCount = data.length;
    const frequencies = [523.25, 659.25, 783.99]; // C, E, G chord

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const progress = t / duration;
      let sample = 0;

      for (const freq of frequencies) {
        const amplitude = Math.exp(-progress * 5); // Decay
        sample += Math.sin(2 * Math.PI * freq * t) * amplitude;
      }

      data[i] = (sample / frequencies.length) * 0.3;
    }
  }

  /**
   * Generate celebration chord
   */
  private generateCelebrationChord(data: Float32Array, sampleRate: number, duration: number): void {
    const frameCount = data.length;
    const frequencies = [523.25, 659.25, 783.99, 1046.5]; // C, E, G, C chord

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const progress = t / duration;
      let sample = 0;

      for (const freq of frequencies) {
        const amplitude = Math.exp(-progress * 3); // Slower decay
        sample += Math.sin(2 * Math.PI * freq * t) * amplitude;
      }

      data[i] = (sample / frequencies.length) * 0.4;
    }
  }

  /**
   * Generate whoosh sound
   */
  private generateWhoosh(data: Float32Array, sampleRate: number, duration: number): void {
    const frameCount = data.length;
    const startFreq = 200;
    const endFreq = 1000;

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const progress = t / duration;
      const frequency = startFreq + (endFreq - startFreq) * progress;
      const amplitude = Math.sin(Math.PI * progress) * 0.3;

      // Add noise for whoosh effect
      const noise = (Math.random() * 2 - 1) * 0.1;
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude + noise;
    }
  }

  /**
   * Generate pop sound
   */
  private generatePop(data: Float32Array, sampleRate: number, _duration: number): void {
    const frameCount = data.length;
    const frequency = 600;

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const amplitude = Math.exp(-t * 50); // Fast decay
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude * 0.4;
    }
  }

  /**
   * Generate swipe sound
   */
  private generateSwipe(data: Float32Array, sampleRate: number, duration: number): void {
    const frameCount = data.length;
    const frequency = 300;

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const progress = t / duration;
      const amplitude = Math.sin(Math.PI * progress) * 0.2;
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude;
    }
  }

  /**
   * Generate buzz sound
   */
  private generateBuzz(data: Float32Array, sampleRate: number, _duration: number): void {
    const frameCount = data.length;
    const frequency = 100;

    for (let i = 0; i < frameCount; i++) {
      const t = i / sampleRate;
      const amplitude = Math.sin(Math.PI * t * 10) * 0.3; // Vibrating amplitude
      data[i] = Math.sin(2 * Math.PI * frequency * t) * amplitude;
    }
  }

  /**
   * Set volume
   */
  setVolume(volume: number): void {
    this.volume = Math.max(0, Math.min(1, volume));
    logger.debug('Volume set', { volume: this.volume });
  }

  /**
   * Set muted state
   */
  setMuted(muted: boolean): void {
    this.muted = muted;
    logger.debug('Muted state changed', { muted });
  }

  /**
   * Set enabled state
   */
  setEnabled(enabled: boolean): void {
    this.enabled = enabled;
    logger.debug('Enabled state changed', { enabled });
  }

  /**
   * Set haptic enabled state
   */
  setHapticEnabled(enabled: boolean): void {
    this.hapticEnabled = enabled;
    logger.debug('Haptic enabled state changed', { enabled });
  }

  /**
   * Set respect reduced motion
   */
  setRespectReducedMotion(respect: boolean): void {
    this.respectReducedMotion = respect;
    logger.debug('Respect reduced motion changed', { respect });
  }

  /**
   * Update reduced motion state (called when user preference changes)
   */
  updateReducedMotion(): void {
    // This method is called to update internal state if needed
    // The actual check happens in play() method
  }

  /**
   * Clear sound cache
   */
  clearCache(): void {
    this.soundCache.clear();
  }
}

// Singleton instance
let soundService: SoundFeedbackService | null = null;

/**
 * Get sound feedback service instance
 */
export function getSoundFeedbackService(): SoundFeedbackService {
  if (!soundService) {
    soundService = new SoundFeedbackService();
  }
  return soundService;
}

/**
 * Play sound (convenience function)
 */
export async function playSound(
  soundType: SoundType,
  options: { haptic?: boolean } = {}
): Promise<void> {
  return getSoundFeedbackService().play(soundType, options);
}

/**
 * Effect Telemetry Hook
 *
 * Tracks effect performance metrics: duration, dropped frames, device Hz
 * Logs to structured logger (no PII)
 * Integrates with useDeviceRefreshRate for accurate Hz detection
 *
 * Location: apps/web/src/effects/core/use-effect-telemetry.ts
 */

import { useRef, useCallback, useEffect } from 'react';
import { createLogger } from '@/lib/logger';
import { usePrefersReducedMotion } from '@/utils/reduced-motion';
import { useDeviceRefreshRate } from '@/hooks/use-device-refresh-rate';
import { logEffectStart, logEffectEnd, logEffectError } from '@/effects/chat/core/telemetry';
import type { EffectName } from '@/effects/chat/core/telemetry';

const logger = createLogger('EffectTelemetry');

export interface EffectTelemetryEvent {
  effect: string;
  startedAt: number;
  endedAt?: number;
  durationMs?: number;
  droppedFrames?: number;
  deviceHz?: number;
  reducedMotion: boolean;
  success: boolean;
}

export interface UseEffectTelemetryOptions {
  effectName: EffectName | string;
  enabled?: boolean;
  alertOnThreshold?: boolean;
  maxDroppedFrames?: number;
  maxDurationMs?: number;
}

export interface UseEffectTelemetryReturn {
  start: () => string | null;
  end: (success?: boolean, error?: Error) => void;
  getDeviceHz: () => number;
  getDroppedFrames: () => number;
}

/**
 * Performance thresholds for alerts
 */
const DEFAULT_THRESHOLDS = {
  maxDroppedFrames: 2,
  maxDurationMs: 300,
};

/**
 * Track performance regressions
 */
const performanceHistory: {
  effect: string;
  durationMs: number;
  droppedFrames: number;
  timestamp: number;
}[] = [];

const MAX_HISTORY_SIZE = 100;

/**
 * Check for performance regression
 */
function checkPerformanceRegression(
  effect: string,
  durationMs: number,
  droppedFrames: number
): boolean {
  // Get recent history for this effect
  const recent = performanceHistory
    .filter((e) => e.effect === effect && Date.now() - e.timestamp < 3600000) // Last hour
    .slice(-10); // Last 10 runs

  if (recent.length < 3) {
    return false; // Not enough data
  }

  const avgDuration = recent.reduce((sum, e) => sum + e.durationMs, 0) / recent.length;
  const avgDroppedFrames = recent.reduce((sum, e) => sum + e.droppedFrames, 0) / recent.length;

  // Check if current performance is significantly worse
  const durationRegression = durationMs > avgDuration * 1.5;
  const framesRegression = droppedFrames > avgDroppedFrames * 2;

  return durationRegression || framesRegression;
}

/**
 * Hook to track effect telemetry
 */
export function useEffectTelemetry(options: UseEffectTelemetryOptions): UseEffectTelemetryReturn {
  const {
    effectName,
    enabled = true,
    alertOnThreshold = true,
    maxDroppedFrames = DEFAULT_THRESHOLDS.maxDroppedFrames,
    maxDurationMs = DEFAULT_THRESHOLDS.maxDurationMs,
  } = options;

  const reducedMotion = usePrefersReducedMotion();
  const { hz: deviceHz } = useDeviceRefreshRate();
  const startTimeRef = useRef<number | null>(null);
  const frameCountRef = useRef(0);
  const lastFrameTimeRef = useRef<number | null>(null);
  const effectIdRef = useRef<string | null>(null);
  const animationFrameIdRef = useRef<number | null>(null);
  const expectedFrameTimeRef = useRef<number>(1000 / 60);

  // Update expected frame time when device Hz changes
  useEffect(() => {
    expectedFrameTimeRef.current = 1000 / deviceHz;
  }, [deviceHz]);

  const start = useCallback((): string | null => {
    if (!enabled) {
      return null;
    }

    const startedAt = performance.now();
    startTimeRef.current = startedAt;
    frameCountRef.current = 0;
    lastFrameTimeRef.current = null;

    // Start tracking with telemetry system
    try {
      effectIdRef.current = logEffectStart(effectName as EffectName, {
        deviceHz: deviceHz as 60 | 120,
        reducedMotion,
      });
    } catch (error) {
      logger.warn('Failed to log effect start', { error, effectName });
      effectIdRef.current = null;
    }

    // Track frame timing with accurate Hz detection
    const trackFrame = (timestamp: number): void => {
      if (startTimeRef.current === null) {
        return; // Effect ended, stop tracking
      }

      if (lastFrameTimeRef.current !== null) {
        const frameDelta = timestamp - lastFrameTimeRef.current;
        const expectedFrameTime = expectedFrameTimeRef.current;

        // If frame took significantly longer than expected, count as dropped
        if (frameDelta > expectedFrameTime * 1.5) {
          const dropped = Math.floor(frameDelta / expectedFrameTime - 1);
          frameCountRef.current += Math.max(0, dropped);
        }
      }

      lastFrameTimeRef.current = timestamp;
      animationFrameIdRef.current = requestAnimationFrame(trackFrame);
    };

    animationFrameIdRef.current = requestAnimationFrame(trackFrame);

    return effectIdRef.current;
  }, [enabled, effectName, deviceHz, reducedMotion]);

  const end = useCallback(
    (success = true, error?: Error): void => {
      if (!enabled || startTimeRef.current === null) {
        return;
      }

      // Stop frame tracking
      if (animationFrameIdRef.current !== null) {
        cancelAnimationFrame(animationFrameIdRef.current);
        animationFrameIdRef.current = null;
      }

      const endedAt = performance.now();
      const durationMs = endedAt - startTimeRef.current;
      const droppedFrames = frameCountRef.current;

      // Check thresholds
      const exceedsThresholds =
        droppedFrames > maxDroppedFrames || durationMs > maxDurationMs;

      // Check for performance regression
      const hasRegression = checkPerformanceRegression(
        effectName,
        durationMs,
        droppedFrames
      );

      // Add to history
      performanceHistory.push({
        effect: effectName,
        durationMs,
        droppedFrames,
        timestamp: Date.now(),
      });

      // Trim history
      if (performanceHistory.length > MAX_HISTORY_SIZE) {
        performanceHistory.shift();
      }

      // Log to telemetry system
      if (effectIdRef.current) {
        try {
          if (error) {
            logEffectError(effectIdRef.current, error, {
              droppedFrames,
              deviceHz: deviceHz as 60 | 120,
              reducedMotion,
            });
          } else {
            logEffectEnd(effectIdRef.current, {
              droppedFrames,
              deviceHz: deviceHz as 60 | 120,
              reducedMotion,
              success,
            });
          }
        } catch (err) {
          logger.warn('Failed to log effect end', { error: err, effectName });
        }
      }

      const event: EffectTelemetryEvent = {
        effect: effectName,
        startedAt: startTimeRef.current,
        endedAt,
        durationMs,
        droppedFrames,
        deviceHz,
        reducedMotion,
        success,
      };

      // Log with appropriate level
      if (error || !success) {
        logger.error('Effect failed', error, {
          effect: event.effect,
          durationMs: event.durationMs,
          droppedFrames: event.droppedFrames,
          deviceHz: event.deviceHz,
        });
      } else if (exceedsThresholds || hasRegression) {
        logger.warn('Effect performance issue', {
          effect: event.effect,
          durationMs: event.durationMs,
          droppedFrames: event.droppedFrames,
          deviceHz: event.deviceHz,
          exceedsThresholds,
          hasRegression,
        });

        // Alert if enabled
        if (alertOnThreshold) {
          if (exceedsThresholds) {
            logger.warn('Effect exceeded thresholds', {
              effect: event.effect,
              droppedFrames,
              maxDroppedFrames,
              durationMs,
              maxDurationMs,
            });
          }

          if (hasRegression) {
            logger.warn('Performance regression detected', {
              effect: event.effect,
              durationMs,
              droppedFrames,
            });
          }
        }
      } else {
        logger.info('Effect completed', {
          effect: event.effect,
          durationMs: event.durationMs,
          droppedFrames: event.droppedFrames,
          deviceHz: event.deviceHz,
          reducedMotion: event.reducedMotion,
          success: event.success,
        });
      }

      // Reset
      startTimeRef.current = null;
      frameCountRef.current = 0;
      lastFrameTimeRef.current = null;
      effectIdRef.current = null;
    },
    [
      enabled,
      effectName,
      reducedMotion,
      deviceHz,
      alertOnThreshold,
      maxDroppedFrames,
      maxDurationMs,
    ]
  );

  const getDeviceHz = useCallback((): number => {
    return deviceHz;
  }, [deviceHz]);

  const getDroppedFrames = useCallback((): number => {
    return frameCountRef.current;
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return (): void => {
      if (animationFrameIdRef.current !== null) {
        cancelAnimationFrame(animationFrameIdRef.current);
      }
    };
  }, []);

  return {
    start,
    end,
    getDeviceHz,
    getDroppedFrames,
  };
}

/**
 * Drag & Drop Support Hook (Web)
 *
 * Provides drag and drop functionality for:
 * - Drag images to upload
 * - Drag messages to reply
 * - Visual feedback during drag
 *
 * Location: apps/web/src/effects/gestures/use-drag-drop.ts
 */

'use client';

import { useState, useCallback, useRef, useEffect } from 'react';
import { createLogger } from '@/lib/logger';
import { useUIConfig } from '@/hooks/use-ui-config';
import { announceToScreenReader } from '@/lib/accessibility';

const logger = createLogger('drag-drop');

/**
 * Keyboard alternative configuration for drag operations (WCAG 2.5.7)
 */
export interface KeyboardAlternativeConfig {
  readonly enabled: boolean;
  readonly keys: readonly string[]; // e.g., ['ArrowUp', 'ArrowDown', 'Enter']
  readonly onKeyboardMove?: (direction: 'up' | 'down' | 'left' | 'right') => void;
  readonly onKeyboardSelect?: () => void;
  readonly onKeyboardCancel?: () => void;
}

/**
 * Drag and drop options
 */
export interface UseDragDropOptions {
  enabled?: boolean;
  onDrop?: (files: File[]) => void;
  onDragEnter?: () => void;
  onDragLeave?: () => void;
  accept?: string[]; // MIME types or file extensions
  multiple?: boolean;
  keyboardAlternative?: KeyboardAlternativeConfig;
  undoable?: boolean; // WCAG 2.5.7: Drag operations must be undoable
  requireConfirmation?: boolean; // WCAG 2.5.7: Require confirmation before drop
}

/**
 * Drag and drop return type
 */
export interface UseDragDropReturn {
  isDragging: boolean;
  dragOver: boolean;
  dragHandlers: {
    onDragEnter: (e: React.DragEvent) => void;
    onDragOver: (e: React.DragEvent) => void;
    onDragLeave: (e: React.DragEvent) => void;
    onDrop: (e: React.DragEvent) => void;
  };
  keyboardHandlers: {
    onKeyDown: (e: React.KeyboardEvent) => void;
    onKeyUp: (e: React.KeyboardEvent) => void;
  };
  undo: () => void;
  canUndo: boolean;
}

const DEFAULT_ENABLED = true;
const DEFAULT_MULTIPLE = true;

export function useDragDrop(options: UseDragDropOptions = {}): UseDragDropReturn {
  const {
    enabled = DEFAULT_ENABLED,
    onDrop,
    onDragEnter,
    onDragLeave,
    accept = ['image/*', 'video/*'],
    multiple = DEFAULT_MULTIPLE,
    keyboardAlternative,
    undoable = false,
    requireConfirmation = false,
  } = options;

  const { visual: _visual, feedback } = useUIConfig();
  const [isDragging, setIsDragging] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const dragCounter = useRef(0);
  const lastDropRef = useRef<File[] | null>(null);
  const [canUndo, setCanUndo] = useState(false);
  const fileInputRef = useRef<HTMLInputElement | null>(null);

  const handleDragEnter = useCallback(
    (e: React.DragEvent) => {
      if (!enabled) {
        return;
      }

      e.preventDefault();
      e.stopPropagation();

      dragCounter.current++;

      if (e.dataTransfer.types.includes('Files')) {
        setIsDragging(true);
        setDragOver(true);
        onDragEnter?.();

        logger.debug('Drag entered', {
          types: e.dataTransfer.types,
        });
      }
    },
    [enabled, onDragEnter]
  );

  const handleDragOver = useCallback(
    (e: React.DragEvent) => {
      if (!enabled || !isDragging) {
        return;
      }

      e.preventDefault();
      e.stopPropagation();

      // Set drop effect
      e.dataTransfer.dropEffect = 'copy';

      setDragOver(true);
    },
    [enabled, isDragging]
  );

  const handleDragLeave = useCallback(
    (e: React.DragEvent) => {
      if (!enabled) {
        return;
      }

      e.preventDefault();
      e.stopPropagation();

      dragCounter.current--;

      if (dragCounter.current === 0) {
        setIsDragging(false);
        setDragOver(false);
        onDragLeave?.();

        logger.debug('Drag left');
      }
    },
    [enabled, onDragLeave]
  );

  const handleDrop = useCallback(
    (e: React.DragEvent) => {
      if (!enabled) {
        return;
      }

      e.preventDefault();
      e.stopPropagation();

      dragCounter.current = 0;
      setIsDragging(false);
      setDragOver(false);

      const files = Array.from(e.dataTransfer.files);

      // Filter files by accepted types
      const acceptedFiles = files.filter((file) => {
        if (accept.length === 0) {
          return true;
        }

        return accept.some((pattern) => {
          if (pattern.includes('/*')) {
            // MIME type pattern (e.g., "image/*")
            const type = pattern.split('/')[0];
            return file.type.startsWith(type + '/');
          } else if (pattern.startsWith('.')) {
            // File extension (e.g., ".jpg")
            return file.name.toLowerCase().endsWith(pattern.toLowerCase());
          } else {
            // Exact MIME type
            return file.type === pattern;
          }
        });
      });

      if (acceptedFiles.length === 0) {
        logger.warn('No accepted files in drop', {
          totalFiles: files.length,
          accept,
        });
        return;
      }

      const filesToProcess = multiple ? acceptedFiles : acceptedFiles.slice(0, 1);

      logger.info('Files dropped', {
        count: filesToProcess.length,
        files: filesToProcess.map((f) => ({ name: f.name, type: f.type, size: f.size })),
      });

      // Trigger haptic feedback if enabled
      if (feedback.haptics) {
        // Web haptics via vibration API
        if ('vibrate' in navigator) {
          navigator.vibrate(10);
        }
      }

      // Store last drop for undo
      if (undoable) {
        lastDropRef.current = filesToProcess;
        setCanUndo(true);
      }

      // Require confirmation if specified
      if (requireConfirmation) {
        const confirmed = window.confirm(
          `Drop ${filesToProcess.length} file(s)? This action can be undone.`
        );
        if (!confirmed) {
          logger.debug('Drop cancelled by user');
          return;
        }
      }

      onDrop?.(filesToProcess);

      // Announce to screen reader
      announceToScreenReader(
        `Dropped ${filesToProcess.length} file${filesToProcess.length > 1 ? 's' : ''}. ${undoable ? 'Press Ctrl+Z to undo.' : ''}`,
        'polite'
      );
    },
    [enabled, accept, multiple, onDrop, feedback, undoable, requireConfirmation]
  );

  // Keyboard handlers for drag alternatives (WCAG 2.5.7)
  const handleKeyboardDown = useCallback(
    (e: React.KeyboardEvent) => {
      if (!keyboardAlternative?.enabled || !enabled) {
        return;
      }

      // Handle file input trigger (Space/Enter opens file picker)
      if ((e.key === 'Enter' || e.key === ' ') && !fileInputRef.current) {
        e.preventDefault();

        // Create hidden file input for keyboard file selection
        const input = document.createElement('input');
        input.type = 'file';
        input.multiple = multiple;
        input.accept = accept.join(',');
        input.style.position = 'absolute';
        input.style.left = '-9999px';
        input.style.opacity = '0';
        input.setAttribute('aria-label', 'Select files to upload');

        input.onchange = (event) => {
          const target = event.target as HTMLInputElement;
          const files = target.files ? Array.from(target.files) : [];
          if (files.length > 0) {
            const filesToProcess = multiple ? files : files.slice(0, 1);
            lastDropRef.current = filesToProcess;
            if (undoable) {
              setCanUndo(true);
            }
            onDrop?.(filesToProcess);
            announceToScreenReader(
              `Selected ${filesToProcess.length} file${filesToProcess.length > 1 ? 's' : ''}. ${undoable ? 'Press Ctrl+Z to undo.' : ''}`,
              'polite'
            );
          }
          document.body.removeChild(input);
          fileInputRef.current = null;
        };

        document.body.appendChild(input);
        fileInputRef.current = input;
        input.click();
        return;
      }

      // Handle arrow key movement
      if (keyboardAlternative.onKeyboardMove) {
        switch (e.key) {
          case 'ArrowUp':
            e.preventDefault();
            keyboardAlternative.onKeyboardMove('up');
            announceToScreenReader('Move up', 'polite');
            break;
          case 'ArrowDown':
            e.preventDefault();
            keyboardAlternative.onKeyboardMove('down');
            announceToScreenReader('Move down', 'polite');
            break;
          case 'ArrowLeft':
            e.preventDefault();
            keyboardAlternative.onKeyboardMove('left');
            announceToScreenReader('Move left', 'polite');
            break;
          case 'ArrowRight':
            e.preventDefault();
            keyboardAlternative.onKeyboardMove('right');
            announceToScreenReader('Move right', 'polite');
            break;
        }
      }

      // Handle selection
      if (e.key === 'Enter' && keyboardAlternative.onKeyboardSelect) {
        e.preventDefault();
        keyboardAlternative.onKeyboardSelect();
        announceToScreenReader('Selected', 'polite');
      }

      // Handle cancel
      if (e.key === 'Escape' && keyboardAlternative.onKeyboardCancel) {
        e.preventDefault();
        keyboardAlternative.onKeyboardCancel();
        announceToScreenReader('Cancelled', 'polite');
      }
    },
    [keyboardAlternative, enabled, multiple, accept, onDrop, undoable]
  );

  const handleKeyboardUp = useCallback(
    (e: React.KeyboardEvent) => {
      // Handle undo (Ctrl+Z or Cmd+Z)
      if ((e.ctrlKey || e.metaKey) && e.key === 'z' && undoable && canUndo) {
        e.preventDefault();
        undo();
      }
    },
    [undoable, canUndo]
  );

  // Undo function
  const undo = useCallback(() => {
    if (lastDropRef.current && undoable) {
      logger.debug('Undoing last drop', {
        fileCount: lastDropRef.current.length,
      });
      lastDropRef.current = null;
      setCanUndo(false);
      announceToScreenReader('Upload undone', 'polite');
      // Note: Actual undo logic should be implemented by the parent component
      // This just provides the framework for undo support
    }
  }, [undoable]);

  // Cleanup file input on unmount
  useEffect(() => {
    return () => {
      if (fileInputRef.current && fileInputRef.current.parentNode) {
        fileInputRef.current.parentNode.removeChild(fileInputRef.current);
      }
    };
  }, []);

  return {
    isDragging,
    dragOver,
    dragHandlers: {
      onDragEnter: handleDragEnter,
      onDragOver: handleDragOver,
      onDragLeave: handleDragLeave,
      onDrop: handleDrop,
    },
    keyboardHandlers: {
      onKeyDown: handleKeyboardDown,
      onKeyUp: handleKeyboardUp,
    },
    undo,
    canUndo,
  };
}

'use client';

import {
  withSpring,
  withTiming,
  withDelay,
  Easing,
  type WithSpringConfig,
  type WithTimingConfig,
} from '@petspark/motion';

export interface SpringConfig {
  damping?: number;
  stiffness?: number;
  mass?: number;
}

export interface TimingConfig {
  duration?: number;
  easing?: (value: number) => number;
}

export const springConfigs = {
  gentle: { damping: 30, stiffness: 300, mass: 0.8 } as SpringConfig,
  smooth: { damping: 25, stiffness: 400 } as SpringConfig,
  bouncy: { damping: 15, stiffness: 500 } as SpringConfig,
  snappy: { damping: 20, stiffness: 600 } as SpringConfig,
};

export const timingConfigs = {
  fast: { duration: 150, easing: Easing.ease } as TimingConfig,
  smooth: { duration: 300, easing: Easing.inOut(Easing.ease) } as TimingConfig,
  slow: { duration: 500, easing: Easing.inOut(Easing.ease) } as TimingConfig,
  elastic: { duration: 400, easing: Easing.elastic(1) } as TimingConfig,
};

export function createSpringTransition(config: SpringConfig = springConfigs.smooth) {
  return (value: number) => withSpring(value, config as WithSpringConfig);
}

export function createTimingTransition(config: TimingConfig = timingConfigs.smooth) {
  return (value: number) => withTiming(value, config as WithTimingConfig);
}

export function createDelayedTransition(delay: number, transition: (value: number) => unknown) {
  return (value: number) =>
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    withDelay(delay, { target: value, transition: transition(value) } as any);
}

export const fadeIn = {
  opacity: {
    from: 0,
    to: 1,
    config: timingConfigs.smooth,
  },
};

export const fadeOut = {
  opacity: {
    from: 1,
    to: 0,
    config: timingConfigs.fast,
  },
};

export const slideUp = {
  translateY: {
