    config: springConfigs.smooth,
  },
  opacity: {
    from: 0,
    to: 1,
    config: timingConfigs.smooth,
  },
};

export const slideDown = {
  translateY: {
    from: -20,
    to: 0,
    config: springConfigs.smooth,
  },
  opacity: {
    from: 0,
    to: 1,
    config: timingConfigs.smooth,
  },
};

export const scaleIn = {
  scale: {
    from: 0.9,
    to: 1,
    config: springConfigs.bouncy,
  },
  opacity: {
    from: 0,
