  },
};

'use client';

import { useCallback, useRef } from 'react';
import {
  useSharedValue,
  useAnimatedStyle,
  withSpring,
  withTiming,
  interpolate,
  Extrapolation,
} from '@petspark/motion';
import { isTruthy } from '@petspark/shared';
import { springConfigs, timingConfigs } from './transitions';
import type { AnimatedStyle } from './animated-view';
import { useReducedMotion } from '@/hooks/useReducedMotion';

export interface UsePullToRefreshOptions {
  onRefresh: () => Promise<void> | void;
  threshold?: number;
  maxDistance?: number;
  enabled?: boolean;
}

export interface UsePullToRefreshReturn {
  pullDistance: ReturnType<typeof useSharedValue<number>>;
  pullOpacity: ReturnType<typeof useSharedValue<number>>;
  pullRotation: ReturnType<typeof useSharedValue<number>>;
  pullScale: ReturnType<typeof useSharedValue<number>>;
  isRefreshing: boolean;
  animatedStyle: AnimatedStyle;
  indicatorStyle: AnimatedStyle;
  handleTouchStart: (e: globalThis.TouchEvent) => void;
  handleTouchMove: (e: globalThis.TouchEvent) => void;
  handleTouchEnd: () => void;
}

export function usePullToRefresh({
  onRefresh,
  threshold = 80,
  maxDistance = 120,
  enabled = true,
}: UsePullToRefreshOptions): UsePullToRefreshReturn {
  const reducedMotion = useReducedMotion();
  const pullDistance = useSharedValue(0);
  const isRefreshing = useRef(false);
  const containerRef = useRef<HTMLDivElement | null>(null);
  const startY = useRef(0);
  const isPulling = useRef(false);

  const pullOpacity = useSharedValue(0);
  const pullRotation = useSharedValue(0);
  const pullScale = useSharedValue(0.5);

  const handleTouchStart = useCallback(
    (e: globalThis.TouchEvent): void => {
      if (!enabled || !containerRef.current) return;

      if (containerRef.current.scrollTop === 0) {
        const firstTouch = e.touches[0];
        if (!firstTouch) {
          return;
        }

        startY.current = firstTouch.clientY;
        isPulling.current = true;
      }
    },
    [enabled]
  );

  const handleTouchMove = useCallback(
    (e: globalThis.TouchEvent): void => {
      if (!enabled || !isPulling.current || !containerRef.current) return;

      const firstTouch = e.touches[0];
      if (!firstTouch) {
        return;
      }

      const currentY = firstTouch.clientY;
      const diff = currentY - startY.current;

      if (diff > 0 && diff < maxDistance) {
        pullDistance.value = diff;

        // Calculate derived values
        pullOpacity.value = interpolate(diff, [0, threshold], [0, 1], Extrapolation.CLAMP);

        pullRotation.value = interpolate(diff, [0, threshold], [0, 360], Extrapolation.CLAMP);

        pullScale.value = interpolate(diff, [0, threshold], [0.5, 1], Extrapolation.CLAMP);
      }
    },
    [enabled, maxDistance, threshold, pullDistance, pullOpacity, pullRotation, pullScale]
  );

  const handleTouchEnd = useCallback(async (): Promise<void> => {
    if (!enabled || !isPulling.current) return;
    isPulling.current = false;

    const distance = pullDistance.value;

    if (distance > threshold && !isRefreshing.current) {
      isRefreshing.current = true;

      try {
        await onRefresh();
      } finally {
        isRefreshing.current = false;
      }
    }

    // Animate back to zero
    pullDistance.value = withSpring(0, springConfigs.smooth);
    pullOpacity.value = withTiming(0, timingConfigs.fast);
    pullRotation.value = withSpring(0, springConfigs.smooth);
    pullScale.value = withSpring(0.5, springConfigs.smooth);
  }, [enabled, threshold, pullDistance, pullOpacity, pullRotation, pullScale, onRefresh]);

  const animatedStyle = useAnimatedStyle(() => {
    if (isTruthy(reducedMotion)) {
      return {
        transform: 'translateY(0)',
        opacity: 0,
      };
    }

    return {
      transform: `translateY(${pullDistance.value}px)`,
      opacity: pullOpacity.value,
    };
  }) as AnimatedStyle;

  const indicatorStyle = useAnimatedStyle(() => {
    if (isTruthy(reducedMotion)) {
      return {
        transform: `rotate(0deg) scale(0.5)`,
      };
    }

    return {
      transform: `rotate(${pullRotation.value}deg) scale(${pullScale.value})`,
    };
  });

  return {
    pullDistance,
    pullOpacity,
    pullRotation,
    pullScale,
    isRefreshing: isRefreshing.current,
    animatedStyle,
    indicatorStyle,
    handleTouchStart,
    handleTouchMove,
    handleTouchEnd,
  };
}

/**
 * React Query hooks for matches API (Web)
 * Location: apps/web/src/hooks/api/use-matches.ts
 */

import type { UseQueryResult, UseMutationResult } from '@tanstack/react-query';
import { useMutation, useQuery, useQueryClient } from '@tanstack/react-query';
import { queryKeys } from '@/lib/query-client';

export interface Match {
  id: string;
  petId: string;
  matchedAt: string;
  pet?: {
    id: string;
    name: string;
    photos: string[];
    [key: string]: unknown;
  };
  [key: string]: unknown;
}

const API_BASE_URL =
  (import.meta.env.VITE_API_URL) ?? 'https://api.petspark.app';

async function fetchMatches(): Promise<Match[]> {
  const response = await fetch(`${String(API_BASE_URL ?? '')}/api/matches`, {
    method: 'GET',
    headers: { 'Content-Type': 'application/json' },
  });
  if (!response.ok) throw new Error('Failed to fetch matches');
  const data = await response.json();
  return Array.isArray(data) ? data : data.items ?? [];
}

export function useMatches(): UseQueryResult<Match[]> {
  return useQuery({
    queryKey: queryKeys.matches.list,
    queryFn: fetchMatches,
    staleTime: 60_000,
    gcTime: 1_800_000,
  });
}

export function useMatch(matchId: string): UseQueryResult<Match> {
  return useQuery({
    queryKey: queryKeys.matches.detail(matchId),
    queryFn: async () => {
      const response = await fetch(`${String(API_BASE_URL ?? '')}/api/matches/${String(matchId ?? '')}`, {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
      if (!response.ok) throw new Error('Failed to fetch match');
      return response.json();
    },
    enabled: !!matchId,
  });
}

/**
 * Example mutation for matches domain: dismiss a match
 */
export interface DismissMatchInput {
  id: string;
}

async function dismissMatch(id: string): Promise<void> {
  const res = await fetch(`${String(API_BASE_URL ?? '')}/api/matches/${String(id ?? '')}/dismiss`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
  });
  if (!res.ok) throw new Error('Failed to dismiss match');
}

export function useDismissMatch(): UseMutationResult<void, Error, DismissMatchInput> {
  const qc = useQueryClient();
  return useMutation({
    mutationFn: async (input: DismissMatchInput) => dismissMatch(input.id),
    onSuccess: () => {
      // Refresh lists/details after mutation
      void qc.invalidateQueries({ queryKey: queryKeys.matches.list });
    },
  });
}

// apps/web/src/hooks/calls/internal/useSignalingClient.ts
'use client';

import { useEffect, useRef } from 'react';
import type { CallSignal, SignalingConfig } from '@petspark/core';
import { CallSignalingClient } from '@petspark/core';

export interface UseSignalingClientResult {
  readonly signalingClient: CallSignalingClient;
  readonly onSignal: (handler: (signal: CallSignal) => void | Promise<void>) => () => void;
}

export function useSignalingClient(config: SignalingConfig): UseSignalingClientResult {
  const clientRef = useRef<CallSignalingClient | null>(null);

  if (!clientRef.current) {
    clientRef.current = new CallSignalingClient(config);
    clientRef.current.connect();
  }

  const signalingClient = clientRef.current;

  useEffect(
    () => () => {
      signalingClient.disconnect();
    },
    [signalingClient]
  );

  return {
    signalingClient,
    onSignal: (handler) => signalingClient.onSignal(handler),
  };
}

// apps/web/src/hooks/calls/useWebRtcCall.ts
'use client';

import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import type {
  CallAnswerSignal,
  CallCandidateSignal,
  CallDirection,
  CallOfferSignal,
  CallSession,
  CallSignal,
  CallStatus,
  SignalingConfig,
} from '@petspark/core';
import { CallSignalingClient } from '@petspark/core';
import { createLogger } from '@/lib/logger';

const logger = createLogger('useWebRtcCall');

export interface UseWebRtcCallOptions {
  signaling: SignalingConfig;
  localUserId: string;
  localDisplayName: string;
  localAvatarUrl?: string | null;
  iceServers?: RTCIceServer[];
}

export interface WebRtcCallState {
  session: CallSession | null;
  localStream: MediaStream | null;
  remoteStream: MediaStream | null;
  isMuted: boolean;
  isCameraOff: boolean;
  isScreenSharing: boolean;
  status: CallStatus;
}

export interface WebRtcCallControls {
  startOutgoingCall: (params: {
    callId: string;
    remoteUserId: string;
    remoteDisplayName: string;
    remoteAvatarUrl?: string | null;
  }) => Promise<void>;
  handleIncomingOffer: (signal: CallOfferSignal) => void;
  acceptIncomingCall: () => Promise<void>;
  rejectIncomingCall: (reason?: string) => void;
  endCall: (reason?: string) => void;
  toggleMute: () => void;
  toggleCamera: () => void;
  toggleScreenShare: () => Promise<void>;
}

export interface UseWebRtcCallResult extends WebRtcCallState, WebRtcCallControls {
  signalingClient: CallSignalingClient;
}

export function useWebRtcCall(options: UseWebRtcCallOptions): UseWebRtcCallResult {
  const { signaling, localUserId, localDisplayName, localAvatarUrl, iceServers } = options;

  const [session, setSession] = useState<CallSession | null>(null);
  const [localStream, setLocalStream] = useState<MediaStream | null>(null);
  const [remoteStream, setRemoteStream] = useState<MediaStream | null>(null);
  const [status, setStatus] = useState<CallStatus>('idle');
  const [isMuted, setIsMuted] = useState(false);
  const [isCameraOff, setIsCameraOff] = useState(false);
  const [isScreenSharing, setIsScreenSharing] = useState(false);

  const signalingClientRef = useRef<CallSignalingClient | null>(null);
  const peerRef = useRef<RTCPeerConnection | null>(null);
  const pendingRemoteOfferRef = useRef<CallOfferSignal | null>(null);
  const currentCallIdRef = useRef<string | null>(null);
  const remoteUserIdRef = useRef<string | null>(null);

  if (!signalingClientRef.current) {
    signalingClientRef.current = new CallSignalingClient(signaling);
    signalingClientRef.current.connect();
  }
  const signalingClient = signalingClientRef.current;

  const iceConfig = useMemo<RTCConfiguration>(
    () => ({
      iceServers: iceServers ?? [
        { urls: 'stun:stun.l.google.com:19302' },
        { urls: 'stun:stun1.l.google.com:19302' },
      ],
    }),
    [iceServers]
  );

  const ensurePeerConnection = useCallback(() => {
    if (peerRef.current && peerRef.current.connectionState !== 'closed') {
      return peerRef.current;
    }

    const pc = new RTCPeerConnection(iceConfig);

    pc.onicecandidate = (event) => {
      if (!event.candidate || !currentCallIdRef.current || !remoteUserIdRef.current) {
        return;
      }
      const candidateSignal: CallCandidateSignal = {
        type: 'call-candidate',
        callId: currentCallIdRef.current,
        fromUserId: localUserId,
        toUserId: remoteUserIdRef.current,
        candidate: event.candidate,
      };
      try {
        signalingClient.send(candidateSignal);
      } catch (err) {
        logger.error('Failed to send ICE candidate', err);
      }
    };

    pc.ontrack = (event) => {
      const [stream] = event.streams;
      if (!stream) {
        return;
      }
      setRemoteStream(stream);
    };

    pc.onconnectionstatechange = () => {
      logger.debug('Peer connection state', { state: pc.connectionState });
      if (
        pc.connectionState === 'failed' ||
        pc.connectionState === 'disconnected' ||
        pc.connectionState === 'closed'
      ) {
        setStatus((prev: CallStatus) => (prev === 'ended' ? prev : 'failed'));
      }
    };

    peerRef.current = pc;
    return pc;
  }, [iceConfig, localUserId, signalingClient]);

  const stopLocalStream = useCallback(() => {
    setLocalStream((prev) => {
      if (prev) {
        prev.getTracks().forEach((track) => track.stop());
      }
      return null;
    });
  }, []);

  const cleanupPeer = useCallback(() => {
    if (peerRef.current) {
      peerRef.current.getSenders().forEach((sender) => {
        try {
          peerRef.current?.removeTrack(sender);
        } catch {
          // ignore
        }
      });
      peerRef.current.onicecandidate = null;
      peerRef.current.ontrack = null;
      peerRef.current.onconnectionstatechange = null;
      peerRef.current.close();
      peerRef.current = null;
    }
    setRemoteStream((prev: MediaStream | null) => {
      if (prev) {
        prev.getTracks().forEach((track) => track.stop());
      }
      return null;
    });
  }, []);

  const resetSession = useCallback(
    (reason?: string) => {
      stopLocalStream();
      cleanupPeer();
      currentCallIdRef.current = null;
      remoteUserIdRef.current = null;
      pendingRemoteOfferRef.current = null;

      setSession((prev: CallSession | null) =>
        prev
          ? {
              ...prev,
              status: reason ? 'failed' : 'ended',
              endedAt: new Date().toISOString(),
              failureReason: reason ?? prev.failureReason,
            }
          : null
      );
      setStatus(reason ? 'failed' : 'ended');
      setIsMuted(false);
      setIsCameraOff(false);
      setIsScreenSharing(false);
    },
    [cleanupPeer, stopLocalStream]
  );

  const attachLocalStream = useCallback(
    (stream: MediaStream) => {
      setLocalStream(stream);
      const pc = ensurePeerConnection();
      stream.getTracks().forEach((track) => {
        pc.addTrack(track, stream);
      });
    },
    [ensurePeerConnection]
  );

  const createLocalMedia = useCallback(
    async (constraints?: MediaStreamConstraints) => {
      const stream = await navigator.mediaDevices.getUserMedia(
        constraints ?? {
          audio: true,
          video: {
            width: { ideal: 1280 },
            height: { ideal: 720 },
            frameRate: { ideal: 30 },
          },
        }
      );
      attachLocalStream(stream);
      return stream;
    },
    [attachLocalStream]
  );

  const buildSessionParticipants = useCallback(
    (
      direction: CallDirection,
      remoteUserId: string,
      remoteDisplayName: string,
      remoteAvatarUrl?: string | null
    ): CallSession => ({
      id: currentCallIdRef.current ?? crypto.randomUUID(),
      kind: 'direct',
      direction,
      status: 'ringing',
      localParticipant: {
        id: localUserId,
        displayName: localDisplayName,
        avatarUrl: localAvatarUrl,
        isLocal: true,
        microphone: 'enabled',
        camera: 'enabled',
      },
      remoteParticipant: {
        id: remoteUserId,
        displayName: remoteDisplayName,
        avatarUrl: remoteAvatarUrl ?? null,
        isLocal: false,
        microphone: 'enabled',
        camera: 'enabled',
      },
      startedAt: new Date().toISOString(),
    }),
    [localAvatarUrl, localDisplayName, localUserId]
  );

  const startOutgoingCall = useCallback<UseWebRtcCallResult['startOutgoingCall']>(
    async ({ callId, remoteUserId, remoteDisplayName, remoteAvatarUrl }) => {
      try {
        currentCallIdRef.current = callId;
        remoteUserIdRef.current = remoteUserId;

        const pc = ensurePeerConnection();
        const stream = await createLocalMedia();
        logger.debug('Created local media stream for outgoing call', {
          audioTracks: stream.getAudioTracks().length,
          videoTracks: stream.getVideoTracks().length,
        });

        const offer = await pc.createOffer({
          offerToReceiveAudio: true,
          offerToReceiveVideo: true,
        });
        await pc.setLocalDescription(offer);

        const sessionState = buildSessionParticipants(
          'outgoing',
          remoteUserId,
          remoteDisplayName,
          remoteAvatarUrl
        );
        setSession(sessionState);
        setStatus('ringing');

        const offerSignal: CallOfferSignal = {
          type: 'call-offer',
          callId,
          fromUserId: localUserId,
          toUserId: remoteUserId,
          sdp: offer.sdp ?? '',
        };
        signalingClient.send(offerSignal);
      } catch (err) {
        const error = err as Error;
        logger.error('Failed to start outgoing call', error);
        resetSession(error.message);
      }
    },
    [
      buildSessionParticipants,
      createLocalMedia,
      ensurePeerConnection,
      localUserId,
      resetSession,
      signalingClient,
    ]
  );

  const handleIncomingOffer = useCallback<UseWebRtcCallResult['handleIncomingOffer']>(
    (signal) => {
      try {
        currentCallIdRef.current = signal.callId;
        remoteUserIdRef.current = signal.fromUserId;
        pendingRemoteOfferRef.current = signal;

        const incomingSession = buildSessionParticipants(
          'incoming',
          signal.fromUserId,
          'Incoming caller'
        );
        incomingSession.startedAt = incomingSession.startedAt ?? new Date().toISOString();
        setSession(incomingSession);
        setStatus('ringing');
      } catch (err) {
        const error = err as Error;
        logger.error('Failed to handle incoming offer', error);
        resetSession(error.message);
      }
    },
    [buildSessionParticipants, resetSession]
  );

  const acceptIncomingCall = useCallback(async () => {
    const offer = pendingRemoteOfferRef.current;
    const callId = currentCallIdRef.current;
    const remoteUserId = remoteUserIdRef.current;
    if (!offer || !callId || !remoteUserId) {
      return;
    }

    try {
      const pc = ensurePeerConnection();
      const stream = await createLocalMedia();
      logger.debug('Created local media stream for incoming call', {
        audioTracks: stream.getAudioTracks().length,
        videoTracks: stream.getVideoTracks().length,
      });

      await pc.setRemoteDescription(new RTCSessionDescription({ type: 'offer', sdp: offer.sdp }));
      const answer = await pc.createAnswer();
      await pc.setLocalDescription(answer);

      setStatus('connecting');
      setSession((prev) =>
        prev
          ? {
              ...prev,
              status: 'connecting',
              startedAt: prev.startedAt ?? new Date().toISOString(),
            }
          : prev
      );

      const answerSignal: CallAnswerSignal = {
        type: 'call-answer',
        callId,
        fromUserId: localUserId,
        toUserId: remoteUserId,
        sdp: answer.sdp ?? '',
      };
      signalingClient.send(answerSignal);
      pendingRemoteOfferRef.current = null;
    } catch (err) {
      const error = err as Error;
      logger.error('Failed to accept incoming call', error);
      resetSession(error.message);
    }
  }, [createLocalMedia, ensurePeerConnection, localUserId, resetSession, signalingClient]);

  const rejectIncomingCall = useCallback<UseWebRtcCallResult['rejectIncomingCall']>(
    (reason) => {
      const callId = currentCallIdRef.current;
      const remoteUserId = remoteUserIdRef.current;
      if (!callId || !remoteUserId) {
        return;
      }
      try {
        signalingClient.send({
          type: 'call-reject',
          callId,
          fromUserId: localUserId,
          toUserId: remoteUserId,
          reason,
        });
      } catch (err) {
        logger.error('Failed to reject call', err);
      } finally {
        resetSession(reason ?? 'Call rejected');
      }
    },
    [localUserId, resetSession, signalingClient]
  );

  const endCall = useCallback<UseWebRtcCallResult['endCall']>(
    (reason) => {
      const callId = currentCallIdRef.current;
      const remoteUserId = remoteUserIdRef.current;
      if (callId && remoteUserId) {
        try {
          signalingClient.send({
            type: 'call-end',
            callId,
            fromUserId: localUserId,
            toUserId: remoteUserId,
            reason,
          });
        } catch (err) {
          logger.error('Failed to end call', err);
        }
      }
      resetSession(reason);
    },
    [localUserId, resetSession, signalingClient]
  );

  const toggleMute = useCallback(() => {
    setIsMuted((prev) => {
      const next = !prev;
      if (localStream) {
        localStream.getAudioTracks().forEach((track) => {
          track.enabled = !next;
        });
      }
      setSession((prevSession: CallSession | null) =>
        prevSession
          ? {
              ...prevSession,
              localParticipant: {
                ...prevSession.localParticipant,
                microphone: next ? 'muted' : 'enabled',
              },
            }
          : prevSession
      );
      return next;
    });
  }, [localStream]);

  const toggleCamera = useCallback(() => {
    setIsCameraOff((prev) => {
      const next = !prev;
      if (localStream) {
        localStream.getVideoTracks().forEach((track) => {
          track.enabled = !next;
        });
      }
      setSession((prevSession: CallSession | null) =>
        prevSession
          ? {
              ...prevSession,
              localParticipant: {
                ...prevSession.localParticipant,
                camera: next ? 'off' : 'enabled',
              },
            }
          : prevSession
      );
      return next;
    });
  }, [localStream]);

  const toggleScreenShare = useCallback(async () => {
    const pc = peerRef.current;
    if (!pc) {
      return;
    }
    if (isScreenSharing) {
      setIsScreenSharing(false);
      if (!localStream) {
        return;
      }
      const cameraTrack = localStream.getVideoTracks()[0];
      if (!cameraTrack) {
        return;
      }
      const sender = pc.getSenders().find((s) => s.track?.kind === 'video');
      if (sender) {
        await sender.replaceTrack(cameraTrack);
      }
      return;
    }

    try {
      const displayStream = await navigator.mediaDevices.getDisplayMedia({
        video: true,
        audio: false,
      });
      const [displayTrack] = displayStream.getVideoTracks();
      if (!displayTrack) {
        return;
      }
      const sender = pc.getSenders().find((s) => s.track?.kind === 'video');
      if (sender) {
        await sender.replaceTrack(displayTrack);
      }
      setIsScreenSharing(true);

      displayTrack.onended = async () => {
        setIsScreenSharing(false);
        if (!localStream) {
          return;
        }
        const originalTrack = localStream.getVideoTracks()[0];
        if (!originalTrack) {
          return;
        }
        const s = pc.getSenders().find((snd) => snd.track?.kind === 'video');
        if (s) {
          await s.replaceTrack(originalTrack);
        }
      };
    } catch (err) {
      const error = err as Error;
      logger.error('Failed to start screen share', error);
    }
  }, [isScreenSharing, localStream]);

  useEffect(() => {
    const unsubscribe = signalingClient.onSignal(async (signal: CallSignal) => {
      const callId = currentCallIdRef.current;
      if (callId && signal.callId !== callId) {
        return;
      }

      switch (signal.type) {
        case 'call-answer': {
          const pc = ensurePeerConnection();
          await pc.setRemoteDescription(
            new RTCSessionDescription({ type: 'answer', sdp: signal.sdp })
          );
          setStatus('in-call');
          setSession((prev: CallSession | null) =>
            prev
              ? {
                  ...prev,
                  status: 'in-call',
                }
              : prev
          );
          break;
        }
        case 'call-candidate': {
          const pc = ensurePeerConnection();
          try {
            await pc.addIceCandidate(new RTCIceCandidate(signal.candidate));
          } catch (err) {
            logger.error('Failed to add ICE candidate', err);
          }
          break;
        }
        case 'call-end': {
          resetSession(signal.reason ?? 'Remote ended call');
          break;
        }
        case 'call-reject': {
          resetSession(signal.reason ?? 'Call rejected');
          break;
        }
        default:
          break;
      }
    });

    return () => {
      unsubscribe();
    };
  }, [ensurePeerConnection, resetSession, signalingClient]);

  useEffect(
    () => () => {
      stopLocalStream();
      cleanupPeer();
      signalingClient.disconnect();
    },
    [cleanupPeer, signalingClient, stopLocalStream]
  );

  return {
    session,
    localStream,
    remoteStream,
    isMuted,
    isCameraOff,
    isScreenSharing,
    status,
    signalingClient,
    startOutgoingCall,
    handleIncomingOffer,
    acceptIncomingCall,
    rejectIncomingCall,
    endCall,
    toggleMute,
    toggleCamera,
    toggleScreenShare,
  };
}

/**
 * AR Filter Hook (Web)
 *
 * Provides AR filter capabilities with:
 * - Face detection and tracking
 * - Real-time filter application
 * - Filter library management
 * - Performance optimization (60fps target)
 * - Canvas-based rendering
 * - Sticker placement
 *
 * Location: apps/web/src/hooks/media/use-ar-filter.ts
 */

import { useCallback, useEffect, useRef, useState } from 'react';
import { createLogger } from '@/lib/logger';
import { triggerHaptic } from '@/effects/chat/core/haptic-manager';

const logger = createLogger('ar-filter');

/**
 * Face landmark points
 */
export interface FaceLandmarks {
  readonly leftEye: { x: number; y: number };
  readonly rightEye: { x: number; y: number };
  readonly nose: { x: number; y: number };
  readonly mouth: { x: number; y: number };
  readonly chin: { x: number; y: number };
  readonly leftEar: { x: number; y: number };
  readonly rightEar: { x: number; y: number };
}

/**
 * Detected face
 */
export interface DetectedFace {
  readonly id: string;
  readonly bounds: DOMRect;
  readonly landmarks: FaceLandmarks;
  readonly confidence: number;
}

/**
 * AR filter configuration
 */
export interface ARFilter {
  readonly id: string;
  readonly name: string;
  readonly type: 'overlay' | 'mask' | 'effect' | 'sticker';
  readonly imageUrl?: string;
  readonly effect?: (ctx: CanvasRenderingContext2D, face: DetectedFace) => void;
  readonly landmarks?: readonly (keyof FaceLandmarks)[];
}

/**
 * AR filter options
 */
export interface UseARFilterOptions {
  readonly videoSource: MediaStream | null;
  readonly targetFPS?: number;
  readonly faceDetectionInterval?: number; // ms
  readonly enablePerformanceMonitoring?: boolean;
  readonly onFaceDetected?: (faces: readonly DetectedFace[]) => void;
  readonly onError?: (error: Error) => void;
}

/**
 * AR filter return type
 */
export interface UseARFilterReturn {
  readonly canvasRef: React.RefObject<HTMLCanvasElement>;
  readonly isActive: boolean;
  readonly detectedFaces: readonly DetectedFace[];
  readonly currentFilter: ARFilter | null;
  readonly fps: number;
  readonly applyFilter: (filter: ARFilter) => void;
  readonly removeFilter: () => void;
  readonly start: () => void;
  readonly stop: () => void;
}

const DEFAULT_TARGET_FPS = 60;
const DEFAULT_FACE_DETECTION_INTERVAL = 100; // ms

// Predefined AR filters
export const AR_FILTERS: readonly ARFilter[] = [
  {
    id: 'dog-ears',
    name: 'Dog Ears',
    type: 'overlay',
    imageUrl: '/filters/dog-ears.png',
    landmarks: ['leftEar', 'rightEar'],
  },
  {
    id: 'cat-whiskers',
    name: 'Cat Whiskers',
    type: 'overlay',
    imageUrl: '/filters/cat-whiskers.png',
    landmarks: ['nose', 'mouth'],
  },
  {
    id: 'sunglasses',
    name: 'Sunglasses',
    type: 'overlay',
    imageUrl: '/filters/sunglasses.png',
    landmarks: ['leftEye', 'rightEye'],
  },
  {
    id: 'heart-eyes',
    name: 'Heart Eyes',
    type: 'effect',
    landmarks: ['leftEye', 'rightEye'],
    effect: (ctx, face) => {
      // Draw hearts over eyes
      const eyeSize = 30;
      ctx.fillStyle = '#FF1493';

      // Left eye heart
      ctx.beginPath();
      ctx.arc(face.landmarks.leftEye.x - 5, face.landmarks.leftEye.y, eyeSize / 2, 0, Math.PI * 2);
      ctx.arc(face.landmarks.leftEye.x + 5, face.landmarks.leftEye.y, eyeSize / 2, 0, Math.PI * 2);
      ctx.fill();

      // Right eye heart
      ctx.beginPath();
      ctx.arc(
        face.landmarks.rightEye.x - 5,
        face.landmarks.rightEye.y,
        eyeSize / 2,
        0,
        Math.PI * 2
      );
      ctx.arc(
        face.landmarks.rightEye.x + 5,
        face.landmarks.rightEye.y,
        eyeSize / 2,
        0,
        Math.PI * 2
      );
      ctx.fill();
    },
  },
  {
    id: 'sparkle',
    name: 'Sparkle',
    type: 'effect',
    landmarks: ['leftEye', 'rightEye', 'nose'],
    effect: (ctx, face) => {
      // Draw sparkles around face
      const sparkles = [face.landmarks.leftEye, face.landmarks.rightEye, face.landmarks.nose];

      ctx.fillStyle = '#FFD700';
      ctx.strokeStyle = '#FFF';
      ctx.lineWidth = 2;

      sparkles.forEach((point) => {
        for (let i = 0; i < 4; i++) {
          const angle = (i * Math.PI) / 2 + Math.random() * 0.5;
          const distance = 20 + Math.random() * 10;
          const x = point.x + Math.cos(angle) * distance;
          const y = point.y + Math.sin(angle) * distance;

          ctx.beginPath();
          ctx.arc(x, y, 3, 0, Math.PI * 2);
          ctx.fill();
          ctx.stroke();
        }
      });
    },
  },
];

export function useARFilter(options: UseARFilterOptions): UseARFilterReturn {
  const {
    videoSource,
    targetFPS: _targetFPS = DEFAULT_TARGET_FPS,
    faceDetectionInterval = DEFAULT_FACE_DETECTION_INTERVAL,
    enablePerformanceMonitoring = true,
    onFaceDetected,
    onError,
  } = options;

  // State
  const [isActive, setIsActive] = useState(false);
  const [detectedFaces, setDetectedFaces] = useState<readonly DetectedFace[]>([]);
  const [currentFilter, setCurrentFilter] = useState<ARFilter | null>(null);
  const [fps, setFps] = useState(0);

  // Refs
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const animationFrameRef = useRef<number | null>(null);
  const faceDetectionTimerRef = useRef<NodeJS.Timeout | null>(null);
  const lastFrameTimeRef = useRef(0);
  const frameCountRef = useRef(0);
  const filterImagesRef = useRef<Map<string, HTMLImageElement>>(new Map());

  // Preload filter images
  useEffect(() => {
    AR_FILTERS.forEach((filter) => {
      if (filter.imageUrl && !filterImagesRef.current.has(filter.id)) {
        const img = new Image();
        img.src = filter.imageUrl;
        img.onload = () => {
          filterImagesRef.current.set(filter.id, img);
          logger.debug('Filter image loaded', { id: filter.id });
        };
        img.onerror = () => {
          logger.error('Failed to load filter image', { id: filter.id });
        };
      }
    });
  }, []);

  // Simple face detection (mock implementation - would use ML library in production)
  const detectFaces = useCallback((): Promise<readonly DetectedFace[]> => {
    // Feature request: Integrate with actual face detection library (e.g., MediaPipe, face-api.js)
    // See: https://github.com/your-org/petspark/issues/face-detection
    // This is a mock implementation for demonstration

    if (!videoRef.current) {
      return Promise.resolve([]);
    }

    // Mock face detection - center of video
    const video = videoRef.current;
    const centerX = video.videoWidth / 2;
    const centerY = video.videoHeight / 2;
    const faceWidth = 200;
    const faceHeight = 250;

    const mockFace: DetectedFace = {
      id: 'face-1',
      bounds: new DOMRect(centerX - faceWidth / 2, centerY - faceHeight / 2, faceWidth, faceHeight),
      landmarks: {
        leftEye: { x: centerX - 50, y: centerY - 40 },
        rightEye: { x: centerX + 50, y: centerY - 40 },
        nose: { x: centerX, y: centerY },
        mouth: { x: centerX, y: centerY + 40 },
        chin: { x: centerX, y: centerY + 80 },
        leftEar: { x: centerX - 100, y: centerY },
        rightEar: { x: centerX + 100, y: centerY },
      },
      confidence: 0.95,
    };

    return Promise.resolve([mockFace]);
  }, []);

  // Run face detection periodically
  useEffect(() => {
    if (!isActive) {
      return;
    }

    const runDetection = async () => {
      try {
        const faces = await detectFaces();
        setDetectedFaces(faces);
        onFaceDetected?.(faces);

        if (faces.length > 0) {
          logger.debug('Faces detected', { count: faces.length });
        }
      } catch (err) {
        logger.error('Face detection failed', err);
        onError?.(err as Error);
      }
    };

    faceDetectionTimerRef.current = setInterval(runDetection, faceDetectionInterval);

    return () => {
      if (faceDetectionTimerRef.current) {
        clearInterval(faceDetectionTimerRef.current);
      }
    };
  }, [isActive, faceDetectionInterval, detectFaces, onFaceDetected, onError]);

  // Render loop
  const render = useCallback(
    (timestamp: number) => {
      if (!canvasRef.current || !videoRef.current || !isActive) {
        return;
      }

      const canvas = canvasRef.current;
      const video = videoRef.current;
      const ctx = canvas.getContext('2d');

      if (!ctx) {
        return;
      }

      // Calculate FPS
      if (enablePerformanceMonitoring) {
        frameCountRef.current++;
        const elapsed = timestamp - lastFrameTimeRef.current;

        if (elapsed >= 1000) {
          setFps(Math.round((frameCountRef.current * 1000) / elapsed));
          frameCountRef.current = 0;
          lastFrameTimeRef.current = timestamp;
        }
      }

      // Draw video frame
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

      // Apply filter to detected faces
      if (currentFilter && detectedFaces.length > 0) {
        detectedFaces.forEach((face) => {
          if (currentFilter.type === 'overlay' && currentFilter.imageUrl) {
            // Draw overlay image
            const img = filterImagesRef.current.get(currentFilter.id);
            if (img && currentFilter.landmarks) {
              // Position based on landmarks
              const landmarks = currentFilter.landmarks;
              if (landmarks.includes('leftEar') && landmarks.includes('rightEar')) {
                // Draw between ears
                const leftEar = face.landmarks.leftEar;
                const rightEar = face.landmarks.rightEar;
                const width = Math.abs(rightEar.x - leftEar.x) * 1.5;
                const height = width * (img.height / img.width);
                ctx.drawImage(
                  img,
                  leftEar.x - width * 0.25,
                  leftEar.y - height * 0.5,
                  width,
                  height
                );
              } else if (landmarks.includes('leftEye') && landmarks.includes('rightEye')) {
                // Draw over eyes
                const leftEye = face.landmarks.leftEye;
                const rightEye = face.landmarks.rightEye;
                const width = Math.abs(rightEye.x - leftEye.x) * 1.8;
                const height = width * (img.height / img.width);
                ctx.drawImage(
                  img,
                  leftEye.x - width * 0.25,
                  leftEye.y - height * 0.4,
                  width,
                  height
                );
              }
            }
          } else if (currentFilter.type === 'effect' && currentFilter.effect) {
            // Apply custom effect
            currentFilter.effect(ctx, face);
          }
        });
      }

      // Continue loop
      animationFrameRef.current = requestAnimationFrame(render);
    },
    [isActive, currentFilter, detectedFaces, enablePerformanceMonitoring]
  );

  // Setup video source
  useEffect(() => {
    if (!videoSource || !videoRef.current) {
      return;
    }

    const video = videoRef.current;
    video.srcObject = videoSource;
    void video.play().catch((error) => {
      console.warn('Video play failed in AR filter:', error);
    });

    video.onloadedmetadata = () => {
      if (canvasRef.current) {
        canvasRef.current.width = video.videoWidth;
        canvasRef.current.height = video.videoHeight;
      }
    };

    return () => {
      video.srcObject = null;
    };
  }, [videoSource]);

  // Start AR filter
  const start = useCallback(() => {
    if (isActive) {
      return;
    }

    setIsActive(true);
    lastFrameTimeRef.current = performance.now();
    frameCountRef.current = 0;

    triggerHaptic('success');
    logger.debug('AR filter started');

    // Start render loop
    animationFrameRef.current = requestAnimationFrame(render);
  }, [isActive, render]);

  // Stop AR filter
  const stop = useCallback(() => {
    if (!isActive) {
      return;
    }

    setIsActive(false);

    if (animationFrameRef.current) {
      cancelAnimationFrame(animationFrameRef.current);
      animationFrameRef.current = null;
    }

    if (faceDetectionTimerRef.current) {
      clearInterval(faceDetectionTimerRef.current);
      faceDetectionTimerRef.current = null;
    }

    setDetectedFaces([]);

    logger.debug('AR filter stopped');
  }, [isActive]);

  // Apply filter
  const applyFilter = useCallback((filter: ARFilter) => {
    setCurrentFilter(filter);
    triggerHaptic('light');
    logger.debug('Filter applied', { id: filter.id, name: filter.name });
  }, []);

  // Remove filter
  const removeFilter = useCallback(() => {
    setCurrentFilter(null);
    logger.debug('Filter removed');
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stop();
    };
  }, [stop]);

  return {
    canvasRef,
    isActive,
    detectedFaces,
    currentFilter,
    fps,
    applyFilter,
    removeFilter,
    start,
    stop,
  };
}

/**
 * Performance budget monitoring with alerts and throttling
 *
 * Features:
 * - Bundle size tracking
 * - Asset size monitoring
 * - Page load time budgets
 * - Core Web Vitals tracking (LCP, FID, CLS)
 * - Memory usage budgets
 * - Network request budgets
 * - Automatic alerts when budgets exceeded
 * - Historical trend analysis
 *
 * @example
 * ```tsx
 * const perfBudget = usePerformanceBudget({
 *   budgets: {
 *     bundleSize: 250000, // 250KB
 *     firstContentfulPaint: 1800, // 1.8s
 *     largestContentfulPaint: 2500, // 2.5s
 *     cumulativeLayoutShift: 0.1,
 *     firstInputDelay: 100 // 100ms
 *   },
 *   onBudgetExceeded: (metric, value, budget) => {
 *     logWarning(`${metric} exceeded: ${value} > ${budget}`);
 *   }
 * });
 *
 * // Check if within budget
 * const isHealthy = perfBudget.isWithinBudget();
 *
 * // Get violations
 * const violations = perfBudget.state.violations;
 * ```
 */

import { useState, useCallback, useEffect, useRef } from 'react';

// ============================================================================
// Types
// ============================================================================

export interface PerformanceBudgets {
  readonly bundleSize?: number; // bytes
  readonly totalAssetSize?: number; // bytes
  readonly firstContentfulPaint?: number; // ms
  readonly largestContentfulPaint?: number; // ms
  readonly firstInputDelay?: number; // ms
  readonly cumulativeLayoutShift?: number; // score
  readonly timeToInteractive?: number; // ms
  readonly totalBlockingTime?: number; // ms
  readonly speedIndex?: number; // ms
  readonly maxRequests?: number;
  readonly maxMemoryUsage?: number; // MB
}

export interface PerformanceBudgetConfig {
  readonly budgets: PerformanceBudgets;
  readonly onBudgetExceeded?: (metric: string, value: number, budget: number) => void;
  readonly onBudgetWarning?: (
    metric: string,
    value: number,
    budget: number,
    percentage: number
  ) => void;
  readonly warningThreshold?: number; // 0-1, default 0.8 (80%)
  readonly enableAutoTracking?: boolean;
}

export interface BudgetViolation {
  readonly metric: string;
  readonly value: number;
  readonly budget: number;
  readonly exceededBy: number;
  readonly timestamp: number;
}

export interface PerformanceBudgetState {
  readonly metrics: Record<string, number>;
  readonly violations: readonly BudgetViolation[];
  readonly isWithinBudget: boolean;
  readonly budgetHealth: number; // 0-100
  readonly lastCheck: number;
}

export interface WebVitals {
  readonly lcp: number | null; // Largest Contentful Paint
  readonly fid: number | null; // First Input Delay
  readonly cls: number | null; // Cumulative Layout Shift
  readonly fcp: number | null; // First Contentful Paint
  readonly ttfb: number | null; // Time to First Byte
}

// ============================================================================
// Constants
// ============================================================================

const DEFAULT_WARNING_THRESHOLD = 0.8; // 80%
const CHECK_INTERVAL = 10000; // 10 seconds
const HISTORY_SIZE = 100;

// ============================================================================
// Hook Implementation
// ============================================================================

export function usePerformanceBudget(config: PerformanceBudgetConfig) {
  const {
    budgets,
    onBudgetExceeded,
    onBudgetWarning,
    warningThreshold = DEFAULT_WARNING_THRESHOLD,
    enableAutoTracking = true,
  } = config;

  // State
  const [state, setState] = useState<PerformanceBudgetState>({
    metrics: {},
    violations: [],
    isWithinBudget: true,
    budgetHealth: 100,
    lastCheck: Date.now(),
  });

  // Refs
  const checkIntervalRef = useRef<number | null>(null);
  const metricsHistoryRef = useRef<Record<string, number>[]>([]);
  const webVitalsRef = useRef<{
    lcp: number | null;
    fid: number | null;
    cls: number | null;
    fcp: number | null;
    ttfb: number | null;
  }>({
    lcp: null,
    fid: null,
    cls: null,
    fcp: null,
    ttfb: null,
  });

  // ============================================================================
  // Metric Collection
  // ============================================================================

  const collectMetrics = useCallback((): Record<string, number> => {
    const metrics: Record<string, number> = {};

    // Performance timing metrics
    if (performance.timing) {
      const timing = performance.timing;
      const navigationStart = timing.navigationStart;

      if (timing.domContentLoadedEventEnd) {
        metrics.domContentLoaded = timing.domContentLoadedEventEnd - navigationStart;
      }

      if (timing.loadEventEnd) {
        metrics.pageLoad = timing.loadEventEnd - navigationStart;
      }

      if (timing.responseStart) {
        metrics.timeToFirstByte = timing.responseStart - navigationStart;
      }
    }

    // Performance entries
    const paintEntries = performance.getEntriesByType('paint');
    for (const entry of paintEntries) {
      if (entry.name === 'first-contentful-paint') {
        metrics.firstContentfulPaint = entry.startTime;
      }
    }

    // Resource sizes
    const resources = performance.getEntriesByType('resource') as PerformanceResourceTiming[];
    let totalSize = 0;
    let requestCount = 0;

    for (const resource of resources) {
      totalSize += resource.transferSize ?? 0;
      requestCount++;
    }

    metrics.totalAssetSize = totalSize;
    metrics.requestCount = requestCount;

    // Memory usage (if available)
    const perfWithMemory = performance as Performance & { memory?: { usedJSHeapSize: number } };
    if (perfWithMemory.memory) {
      metrics.memoryUsage = perfWithMemory.memory.usedJSHeapSize / 1024 / 1024; // MB
    }

    // Web Vitals
    if (webVitalsRef.current.lcp !== null) {
      metrics.largestContentfulPaint = webVitalsRef.current.lcp;
    }

    if (webVitalsRef.current.fid !== null) {
      metrics.firstInputDelay = webVitalsRef.current.fid;
    }

    if (webVitalsRef.current.cls !== null) {
      metrics.cumulativeLayoutShift = webVitalsRef.current.cls;
    }

    return metrics;
  }, []);

  // ============================================================================
  // Budget Checking
  // ============================================================================

  const checkBudgets = useCallback(() => {
    const metrics = collectMetrics();
    const violations: BudgetViolation[] = [];
    let totalBudgets = 0;
    let violatedBudgets = 0;

    // Check each budget
    for (const [metricKey, budget] of Object.entries(budgets)) {
      if (budget === undefined) continue;

      const value = metrics[metricKey];
      if (value === undefined) continue;

      totalBudgets++;

      // Check if exceeded
      if (value > budget) {
        violatedBudgets++;

        const violation: BudgetViolation = {
          metric: metricKey,
          value,
          budget,
          exceededBy: value - budget,
          timestamp: Date.now(),
        };

        violations.push(violation);

        if (onBudgetExceeded) {
          onBudgetExceeded(metricKey, value, budget);
        }
      }
      // Check warning threshold
      else if (value > budget * warningThreshold) {
        const percentage = (value / budget) * 100;

        if (onBudgetWarning) {
          onBudgetWarning(metricKey, value, budget, percentage);
        }
      }
    }

    // Calculate health score
    const health =
      totalBudgets > 0 ? Math.round(((totalBudgets - violatedBudgets) / totalBudgets) * 100) : 100;

    // Add to history
    metricsHistoryRef.current.push(metrics);
    if (metricsHistoryRef.current.length > HISTORY_SIZE) {
      metricsHistoryRef.current.shift();
    }

    setState({
      metrics,
      violations,
      isWithinBudget: violations.length === 0,
      budgetHealth: health,
      lastCheck: Date.now(),
    });
  }, [budgets, collectMetrics, warningThreshold, onBudgetExceeded, onBudgetWarning]);

  // ============================================================================
  // Web Vitals Tracking
  // ============================================================================

  const trackWebVitals = useCallback(() => {
    // Largest Contentful Paint (LCP)
    const lcpObserver = new PerformanceObserver((entryList) => {
      const entries = entryList.getEntries();
      const lastEntry = entries[entries.length - 1] as PerformancePaintTiming & {
        renderTime?: number;
        loadTime?: number;
      };
      if (lastEntry) {
        webVitalsRef.current.lcp =
          lastEntry.renderTime ?? lastEntry.loadTime ?? lastEntry.startTime;
      }
    });

    try {
      lcpObserver.observe({ type: 'largest-contentful-paint', buffered: true });
    } catch {
      // Not supported
    }

    // First Input Delay (FID)
    const fidObserver = new PerformanceObserver((entryList) => {
      const entries = entryList.getEntries();
      const firstEntry = entries[0] as PerformanceEventTiming;
      if (firstEntry) {
        webVitalsRef.current.fid = firstEntry.processingStart - firstEntry.startTime;
      }
    });

    try {
      fidObserver.observe({ type: 'first-input', buffered: true });
    } catch {
      // Not supported
    }

    // Cumulative Layout Shift (CLS)
    let clsValue = 0;
    const clsObserver = new PerformanceObserver((entryList) => {
      for (const entry of entryList.getEntries()) {
        const layoutShift = entry as PerformanceEntry & {
          hadRecentInput?: boolean;
          value?: number;
        };
        if (!layoutShift.hadRecentInput) {
          clsValue += layoutShift.value ?? 0;
          webVitalsRef.current.cls = clsValue;
        }
      }
    });

    try {
      clsObserver.observe({ type: 'layout-shift', buffered: true });
    } catch {
      // Not supported
    }

    // First Contentful Paint (FCP)
    const fcpObserver = new PerformanceObserver((entryList) => {
      const entries = entryList.getEntries();
      const fcpEntry = entries.find((e) => e.name === 'first-contentful-paint');
      if (fcpEntry) {
        webVitalsRef.current.fcp = fcpEntry.startTime;
      }
    });

    try {
      fcpObserver.observe({ type: 'paint', buffered: true });
    } catch {
      // Not supported
    }

    return () => {
      lcpObserver.disconnect();
      fidObserver.disconnect();
      clsObserver.disconnect();
      fcpObserver.disconnect();
    };
  }, []);

  // ============================================================================
  // Public API
  // ============================================================================

  const isWithinBudget = useCallback(
    (metric?: string): boolean => {
      if (!metric) {
        return state.isWithinBudget;
      }

      const value = state.metrics[metric];
      const budget = budgets[metric as keyof PerformanceBudgets];

      if (value === undefined || budget === undefined) {
        return true;
      }

      return value <= budget;
    },
    [state.isWithinBudget, state.metrics, budgets]
  );

  const getMetricHistory = useCallback((metric: string): readonly number[] => {
    return metricsHistoryRef.current
      .map((m) => m[metric])
      .filter((v): v is number => v !== undefined);
  }, []);

  const getTrend = useCallback(
    (metric: string): 'improving' | 'stable' | 'degrading' => {
      const history = getMetricHistory(metric);
      if (history.length < 2) return 'stable';

      const recent = history.slice(-10);
      const older = history.slice(-20, -10);

      if (recent.length === 0 || older.length === 0) return 'stable';

      const recentAvg = recent.reduce((sum, v) => sum + v, 0) / recent.length;
      const olderAvg = older.reduce((sum, v) => sum + v, 0) / older.length;

      const diff = recentAvg - olderAvg;
      const threshold = olderAvg * 0.05; // 5% threshold

      if (diff < -threshold) return 'improving';
      if (diff > threshold) return 'degrading';
      return 'stable';
    },
    [getMetricHistory]
  );

  const getWebVitals = useCallback((): WebVitals => {
    return webVitalsRef.current;
  }, []);

  const reset = useCallback(() => {
    metricsHistoryRef.current = [];
    webVitalsRef.current = {
      lcp: null,
      fid: null,
      cls: null,
      fcp: null,
      ttfb: null,
    };

    setState({
      metrics: {},
      violations: [],
      isWithinBudget: true,
      budgetHealth: 100,
      lastCheck: Date.now(),
    });
  }, []);

  // ============================================================================
  // Effects
  // ============================================================================

  // Initial check
  useEffect(() => {
    if (enableAutoTracking) {
      // Wait for page load
      if (document.readyState === 'complete') {
        checkBudgets();
      } else {
        window.addEventListener('load', checkBudgets, { once: true });
      }
    }

    return undefined;
  }, [enableAutoTracking, checkBudgets]);

  // Periodic checking
  useEffect(() => {
    if (enableAutoTracking) {
      checkIntervalRef.current = window.setInterval(() => {
        checkBudgets();
      }, CHECK_INTERVAL);

      return () => {
        if (checkIntervalRef.current !== null) {
          clearInterval(checkIntervalRef.current);
        }
      };
    }

    return undefined;
  }, [enableAutoTracking, checkBudgets]);

  // Web Vitals tracking
  useEffect(() => {
    if (enableAutoTracking) {
      return trackWebVitals();
    }

    return undefined;
  }, [enableAutoTracking, trackWebVitals]);

  return {
    checkBudgets,
    isWithinBudget,
    getMetricHistory,
